import React from 'react';

function MobileNav({ activeTab, onTabClick, onHomeSectionChange }) {
    const handleHomeClick = (e) => {
        e.preventDefault();
        onTabClick('홈');
        // 홈은 현재 섹션 유지
    };

    const handleAboutClick = (e) => {
        e.preventDefault();
        onTabClick('홈');
        // 탭 변경 후에 섹션 변경
        setTimeout(() => {
            if (onHomeSectionChange) {
                onHomeSectionChange('notice'); // About → notice (디자이너알기)
            }
        }, 0);
    };

    const handleGalleryClick = (e) => {
        e.preventDefault();
        onTabClick('추첨형'); // 갤러리 탭
    };

    const handleFaqClick = (e) => {
        e.preventDefault();
        onTabClick('홈');
        // 탭 변경 후에 섹션 변경
        setTimeout(() => {
            if (onHomeSectionChange) {
                onHomeSectionChange('faq'); // FAQ → faq (자주하는 질문)
            }
        }, 0);
    };

    const handleContactClick = (e) => {
        e.preventDefault();
        onTabClick('홈');
        // 탭 변경 후에 섹션 변경
        setTimeout(() => {
            if (onHomeSectionChange) {
                onHomeSectionChange('inquiry'); // 문의 → inquiry (문의하기)
            }
        }, 0);
    };

    return (
        <nav className="footer-nav-wrap">
            <div className="ft-nav-list">
                <a href="#"
                   className={`home ${activeTab === '홈' ? 'active' : ''}`}
                   onClick={handleHomeClick}>
                    <i className="home-icon"></i><span>홈</span>
                </a>
                <a href="#"
                   className="about"
                   onClick={handleAboutClick}>
                    <i className="about-icon"></i><span>About</span>
                </a>
                <a href="#"
                   className={`gallery ${activeTab === '추첨형' ? 'active' : ''}`}
                   onClick={handleGalleryClick}>
                    <i className="gallery-icon"></i><span>갤러리</span>
                </a>
                <a href="#"
                   className="faq"
                   onClick={handleFaqClick}>
                    <i className="faq-icon"></i><span>FAQ</span>
                </a>
                <a href="#"
                   className="contact"
                   onClick={handleContactClick}>
                    <i className="contact-icon"></i><span>문의</span>
                </a>
            </div>
        </nav>
    );
}

export default MobileNav;
