import React, { useState } from 'react';

function InquiryForm2() {
    const [activeFilter, setActiveFilter] = useState('진행중');
    const [currentItemIndex, setCurrentItemIndex] = useState(0);
    const [comments, setComments] = useState({});
    const [newComment, setNewComment] = useState('');

    const handleFilterClick = (filterName) => {
        setActiveFilter(filterName);
        setCurrentItemIndex(0); // 카테고리 변경시 첫번째 아이템으로
    };

    // 전체 캠페인 데이터
    const getAllCampaignItems = () => {
        const allItems = {
            '진행중': [
                {
                    id: 1,
                    badge: 'bright-blue',
                    badgeText: '추첨진행중',
                    image: '../static/images/test-05.jpg',
                    mallName: '11번가',
                    title: '[추첨형] 삼성 갤럭시 버즈 프로 무선 이어폰',
                    participants: '1,250명',
                    winners: '10명',
                    deadline: '마감: 2025-07-15 23:59'
                },
                {
                    id: 2,
                    badge: 'bright-blue',
                    badgeText: '추첨진행중',
                    image: '../static/images/test-05.jpg',
                    mallName: '쿠팡',
                    title: '[추첨형] 애플 에어팟 맥스 헤드폰',
                    participants: '892명',
                    winners: '5명',
                    deadline: '마감: 2025-07-20 23:59'
                }
            ],
            '당첨자 발표': [
                {
                    id: 3,
                    badge: 'bright-orange',
                    badgeText: '당첨발표',
                    image: '../static/images/test-05.jpg',
                    mallName: '쿠팡',
                    title: '[추첨형] 다이슨 에어랩 스타일러 세트',
                    participants: '3,420명',
                    winners: '5명',
                    deadline: '발표: 2025-06-30 14:00'
                },
                {
                    id: 4,
                    badge: 'bright-orange',
                    badgeText: '당첨발표',
                    image: '../static/images/test-05.jpg',
                    mallName: '지마켓',
                    title: '[추첨형] 아이폰 15 Pro 케이스 세트',
                    participants: '1,856명',
                    winners: '15명',
                    deadline: '발표: 2025-07-01 10:00'
                }
            ],
            '종료': [
                {
                    id: 5,
                    badge: 'gray',
                    badgeText: '종료',
                    image: '../static/images/test-05.jpg',
                    mallName: '위메프',
                    title: '[추첨형] LG 그램 노트북 15인치',
                    participants: '5,234명',
                    winners: '3명',
                    deadline: '종료일: 2025-06-25 23:59'
                },
                {
                    id: 6,
                    badge: 'gray',
                    badgeText: '종료',
                    image: '../static/images/test-05.jpg',
                    mallName: '티몬',
                    title: '[추첨형] 닌텐도 스위치 OLED 모델',
                    participants: '4,567명',
                    winners: '8명',
                    deadline: '종료일: 2025-06-20 23:59'
                }
            ]
        };
        return allItems[activeFilter] || [];
    };

    const currentItems = getAllCampaignItems();
    const currentItem = currentItems[currentItemIndex];

    // 댓글 데이터 (예시)
    const getCommentsForItem = (itemId) => {
        const defaultComments = {
            1: [
                { id: 1, username: '디자인러버', timeAgo: '2시간 전', content: '이 이어폰 정말 갖고 싶었는데!', avatar: '디' },
                { id: 2, username: '음악매니아', timeAgo: '3시간 전', content: '음질이 어떤가요?', avatar: '음' },
                { id: 3, username: '갤럭시유저', timeAgo: '5시간 전', content: '갤럭시랑 연동 잘 되나요?', avatar: '갤' }
            ],
            2: [
                { id: 4, username: '애플팬', timeAgo: '1시간 전', content: '에어팟 맥스 대박!', avatar: '애' },
                { id: 5, username: '헤드폰수집가', timeAgo: '4시간 전', content: '이거 진짜 좋은 제품이에요', avatar: '헤' }
            ]
        };
        return comments[itemId] || defaultComments[itemId] || [];
    };

    const handlePrevItem = () => {
        if (currentItemIndex > 0) {
            setCurrentItemIndex(currentItemIndex - 1);
        }
    };

    const handleNextItem = () => {
        if (currentItemIndex < currentItems.length - 1) {
            setCurrentItemIndex(currentItemIndex + 1);
        }
    };

    const handleAddComment = () => {
        if (!newComment.trim() || !currentItem) return;

        const newCommentObj = {
            id: Date.now(),
            username: '나',
            timeAgo: '방금 전',
            content: newComment,
            avatar: '나'
        };

        setComments(prev => ({
            ...prev,
            [currentItem.id]: [...(prev[currentItem.id] || getCommentsForItem(currentItem.id)), newCommentObj]
        }));
        setNewComment('');
    };

    if (!currentItem) return <div>Loading...</div>;

    return (
        <>
            <h2 className="h3-title">추첨형 캠페인</h2>

            <div style={{ display: 'flex', gap: '20px', height: '80vh' }}>
                {/* 왼쪽: 카테고리 */}
                <div style={{ width: '200px', flexShrink: 0 }}>
                    <div className="mycampaign-progess">
                        <ul className="tab-style-round" style={{ flexDirection: 'column', gap: '10px' }}>
                            <li className={activeFilter === '진행중' ? 'active' : ''}>
                                <a href="#" onClick={(e) => {e.preventDefault(); handleFilterClick('진행중');}}>
                                    진행중<span>12</span>
                                </a>
                            </li>
                            <li className={activeFilter === '당첨자 발표' ? 'active' : ''}>
                                <a href="#" onClick={(e) => {e.preventDefault(); handleFilterClick('당첨자 발표');}}>
                                    당첨자 발표<span>3</span>
                                </a>
                            </li>
                            <li className={activeFilter === '종료' ? 'active' : ''}>
                                <a href="#" onClick={(e) => {e.preventDefault(); handleFilterClick('종료');}}>
                                    종료<span>8</span>
                                </a>
                            </li>
                        </ul>
                    </div>
                </div>

                {/* 중앙: 현재 아이템 */}
                <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
                    <div className="shadowBox3" style={{ flex: 1, position: 'relative' }}>
                        <div className="item" style={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                            <div className="badge-group">
                                <p className={`badge-pos-left ${currentItem.badge}`}>{currentItem.badgeText}</p>
                            </div>
                            <div className="item-photo-box" style={{ flex: 1, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                                <img src={currentItem.image} className="img" alt="추첨상품" style={{ maxHeight: '300px', objectFit: 'contain' }} />
                            </div>
                            <ul className="item-info-box">
                                <li className="mall-name">{currentItem.mallName}</li>
                                <li className="campaign-title">{currentItem.title}</li>
                                <li className="lottery-info">
                                    <span className="participants">참여자 수: <strong>{currentItem.participants}</strong></span>
                                    <span className="winners">당첨자: <strong>{currentItem.winners}</strong></span>
                                </li>
                                <li className="deadline">{currentItem.deadline}</li>
                            </ul>
                        </div>

                        {/* 네비게이션 버튼 */}
                        {currentItemIndex > 0 && (
                            <button
                                onClick={handlePrevItem}
                                style={{
                                    position: 'absolute',
                                    left: '10px',
                                    top: '50%',
                                    transform: 'translateY(-50%)',
                                    background: 'rgba(0,0,0,0.5)',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '50%',
                                    width: '40px',
                                    height: '40px',
                                    cursor: 'pointer',
                                    fontSize: '18px'
                                }}
                            >
                                ←
                            </button>
                        )}

                        {currentItemIndex < currentItems.length - 1 && (
                            <button
                                onClick={handleNextItem}
                                style={{
                                    position: 'absolute',
                                    right: '10px',
                                    top: '50%',
                                    transform: 'translateY(-50%)',
                                    background: 'rgba(0,0,0,0.5)',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '50%',
                                    width: '40px',
                                    height: '40px',
                                    cursor: 'pointer',
                                    fontSize: '18px'
                                }}
                            >
                                →
                            </button>
                        )}

                        {/* 인디케이터 */}
                        <div style={{
                            position: 'absolute',
                            bottom: '10px',
                            left: '50%',
                            transform: 'translateX(-50%)',
                            display: 'flex',
                            gap: '5px'
                        }}>
                            {currentItems.map((_, index) => (
                                <div
                                    key={index}
                                    style={{
                                        width: '8px',
                                        height: '8px',
                                        borderRadius: '50%',
                                        background: index === currentItemIndex ? '#007bff' : 'rgba(255,255,255,0.5)',
                                        cursor: 'pointer'
                                    }}
                                    onClick={() => setCurrentItemIndex(index)}
                                />
                            ))}
                        </div>
                    </div>
                </div>

                {/* 오른쪽: 댓글 영역 */}
                <div style={{ width: '300px', flexShrink: 0, display: 'flex', flexDirection: 'column' }}>
                    <div className="shadowBox3" style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
                        <div style={{ padding: '20px', borderBottom: '1px solid #eee' }}>
                            <h3 style={{ margin: 0, fontSize: '16px', fontWeight: 'bold' }}>댓글</h3>
                        </div>

                        {/* 댓글 목록 */}
                        <div style={{ flex: 1, overflow: 'auto', padding: '0 20px' }}>
                            {getCommentsForItem(currentItem.id).map(comment => (
                                <div key={comment.id} style={{
                                    display: 'flex',
                                    gap: '10px',
                                    padding: '15px 0',
                                    borderBottom: '1px solid #f5f5f5'
                                }}>
                                    <div style={{
                                        width: '35px',
                                        height: '35px',
                                        borderRadius: '50%',
                                        background: `linear-gradient(45deg, #${Math.floor(Math.random()*16777215).toString(16)}, #${Math.floor(Math.random()*16777215).toString(16)})`,
                                        display: 'flex',
                                        alignItems: 'center',
                                        justifyContent: 'center',
                                        color: 'white',
                                        fontSize: '12px',
                                        fontWeight: 'bold',
                                        flexShrink: 0
                                    }}>
                                        {comment.avatar}
                                    </div>
                                    <div style={{ flex: 1 }}>
                                        <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '5px' }}>
                                            <span style={{ fontWeight: 'bold', fontSize: '14px' }}>{comment.username}</span>
                                            <span style={{ fontSize: '12px', color: '#999' }}>{comment.timeAgo}</span>
                                        </div>
                                        <p style={{ margin: 0, fontSize: '14px', lineHeight: '1.4' }}>{comment.content}</p>
                                    </div>
                                </div>
                            ))}
                        </div>

                        {/* 댓글 작성 */}
                        <div style={{ padding: '20px', borderTop: '1px solid #eee' }}>
                            <div style={{ display: 'flex', gap: '10px' }}>
                                <textarea
                                    value={newComment}
                                    onChange={(e) => setNewComment(e.target.value)}
                                    placeholder="댓글을 입력하세요..."
                                    style={{
                                        flex: 1,
                                        minHeight: '60px',
                                        border: '1px solid #ddd',
                                        borderRadius: '8px',
                                        padding: '10px',
                                        resize: 'none',
                                        fontSize: '14px'
                                    }}
                                />
                                <button
                                    onClick={handleAddComment}
                                    className="btnXl btnMainColor1"
                                    style={{
                                        alignSelf: 'flex-end',
                                        padding: '10px 15px',
                                        minWidth: '60px'
                                    }}
                                >
                                    등록
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default InquiryForm2;
