import React, { useState } from 'react';

function InquiryForm2() {
    const [activeFilter, setActiveFilter] = useState('웹디자인'); // 초기값을 실제 존재하는 키로 설정
    const [currentItemIndex, setCurrentItemIndex] = useState(0);
    const [comments, setComments] = useState({});
    const [newComment, setNewComment] = useState('');

    const handleFilterClick = (filterName) => {
        setActiveFilter(filterName);
        setCurrentItemIndex(0); // 카테고리 변경시 첫번째 아이템으로
    };

    // 전체 캠페인 데이터
    const getAllCampaignItems = () => {
        const allItems = {
            '웹디자인': [
                {
                    id: 1,
                    badge: 'bright-blue',
                    badgeText: '추첨진행중',
                    image: '../static/images/test-05.jpg',
                    mallName: '11번가',
                    title: '[추첨형] 삼성 갤럭시 버즈 프로 무선 이어폰',
                    participants: '1,250명',
                    winners: '10명',
                    deadline: '마감: 2025-07-15 23:59'
                },
                {
                    id: 2,
                    badge: 'bright-blue',
                    badgeText: '추첨진행중',
                    image: '../static/images/test-05.jpg',
                    mallName: '쿠팡',
                    title: '[추첨형] 애플 에어팟 맥스 헤드폰',
                    participants: '892명',
                    winners: '5명',
                    deadline: '마감: 2025-07-20 23:59'
                },
                {
                    id: 7,
                    badge: 'bright-blue',
                    badgeText: '추첨진행중',
                    image: '../static/images/test-05.jpg',
                    mallName: '네이버쇼핑',
                    title: '[추첨형] 아이패드 프로 11인치',
                    participants: '2,340명',
                    winners: '3명',
                    deadline: '마감: 2025-07-25 23:59'
                }
            ],
            'UX/UI디자인': [
                {
                    id: 3,
                    badge: 'bright-orange',
                    badgeText: '당첨발표',
                    image: '../static/images/test-05.jpg',
                    mallName: '쿠팡',
                    title: '[추첨형] 다이슨 에어랩 스타일러 세트',
                    participants: '3,420명',
                    winners: '5명',
                    deadline: '발표: 2025-06-30 14:00'
                },
                {
                    id: 4,
                    badge: 'bright-orange',
                    badgeText: '당첨발표',
                    image: '../static/images/test-05.jpg',
                    mallName: '지마켓',
                    title: '[추첨형] 아이폰 15 Pro 케이스 세트',
                    participants: '1,856명',
                    winners: '15명',
                    deadline: '발표: 2025-07-01 10:00'
                }
            ],
            '그래픽디자인': [
                {
                    id: 5,
                    badge: 'gray',
                    badgeText: '종료',
                    image: '../static/images/test-05.jpg',
                    mallName: '위메프',
                    title: '[추첨형] LG 그램 노트북 15인치',
                    participants: '5,234명',
                    winners: '3명',
                    deadline: '종료일: 2025-06-25 23:59'
                },
                {
                    id: 6,
                    badge: 'gray',
                    badgeText: '종료',
                    image: '../static/images/test-05.jpg',
                    mallName: '티몬',
                    title: '[추첨형] 닌텐도 스위치 OLED 모델',
                    participants: '4,567명',
                    winners: '8명',
                    deadline: '종료일: 2025-06-20 23:59'
                }
            ]
        };
        return allItems[activeFilter] || [];
    };

    const currentItems = getAllCampaignItems();
    const currentItem = currentItems[currentItemIndex];

    // 댓글 데이터 (예시)
    const getCommentsForItem = (itemId) => {
        const defaultComments = {
            1: [
                { id: 1, username: '디자인러버', timeAgo: '2시간 전', content: '이 이어폰 정말 갖고 싶었는데! 당첨되면 좋겠어요 ㅠㅠ', avatar: '디' },
                { id: 2, username: '음악매니아', timeAgo: '3시간 전', content: '음질이 어떤가요? 리뷰 보니까 괜찮던데', avatar: '음' },
                { id: 3, username: '갤럭시유저', timeAgo: '5시간 전', content: '갤럭시랑 연동 잘 되나요?', avatar: '갤' }
            ],
            2: [
                { id: 4, username: '애플팬', timeAgo: '1시간 전', content: '에어팟 맥스 대박! 이거 꼭 갖고 싶다', avatar: '애' },
                { id: 5, username: '헤드폰수집가', timeAgo: '4시간 전', content: '이거 진짜 좋은 제품이에요. 노이즈캔슬링 끝판왕', avatar: '헤' }
            ],
            7: [
                { id: 8, username: '아이패드러버', timeAgo: '30분 전', content: '프로 모델이네요! 애플펜슬도 같이 주나요?', avatar: '아' },
                { id: 9, username: '디지털노마드', timeAgo: '2시간 전', content: '작업용으로 딱인데 당첨되면 좋겠다', avatar: '디' }
            ],
            3: [
                { id: 10, username: '뷰티블로거', timeAgo: '1시간 전', content: '다이슨 에어랩 완전 갖고 싶어요!', avatar: '뷰' }
            ],
            4: [
                { id: 11, username: '아이폰유저', timeAgo: '30분 전', content: '케이스 예쁘네요', avatar: '아' }
            ],
            5: [
                { id: 12, username: '노트북매니아', timeAgo: '2시간 전', content: 'LG 그램 가볍고 좋죠', avatar: '노' }
            ],
            6: [
                { id: 13, username: '게이머', timeAgo: '1시간 전', content: '스위치 OLED 화질 짱이에요', avatar: '게' }
            ]
        };
        return comments[itemId] || defaultComments[itemId] || [];
    };

    const handlePrevItem = () => {
        if (currentItemIndex > 0) {
            setCurrentItemIndex(currentItemIndex - 1);
        }
    };

    const handleNextItem = () => {
        if (currentItemIndex < currentItems.length - 1) {
            setCurrentItemIndex(currentItemIndex + 1);
        }
    };

    const handleAddComment = () => {
        if (!newComment.trim() || !currentItem) return;

        const newCommentObj = {
            id: Date.now(),
            username: '나',
            timeAgo: '방금 전',
            content: newComment,
            avatar: '나'
        };

        const currentComments = comments[currentItem.id] || getCommentsForItem(currentItem.id);

        setComments(prev => ({
            ...prev,
            [currentItem.id]: [...currentComments, newCommentObj]
        }));
        setNewComment('');
    };

    // currentItems가 비어있거나 currentItem이 없는 경우 처리
    if (!currentItems.length || !currentItem) {
        return (
            <div className="outer-layout-bg">
                <div className="mypage-2col-container">
                    <div className="my-lnb-sidebar">
                        <dl className="user-greeting">
                            <dt>추첨형 캠페인</dt>
                            <dd>다양한 상품을 만나보세요</dd>
                        </dl>
                        <div className="code-customer-menu">
                            <a
                                href=""
                                className={activeFilter === '웹디자인' ? 'active' : ''}
                                onClick={(e) => {
                                    e.preventDefault();
                                    handleFilterClick('웹디자인');
                                }}
                            >
                                <i></i>웹디자인
                            </a>
                            <a
                                href=""
                                className={activeFilter === 'UX/UI디자인' ? 'active' : ''}
                                onClick={(e) => {
                                    e.preventDefault();
                                    handleFilterClick('UX/UI디자인');
                                }}
                            >
                                <i></i>UX/UI디자인
                            </a>
                            <a
                                href=""
                                className={activeFilter === '그래픽디자인' ? 'active' : ''}
                                onClick={(e) => {
                                    e.preventDefault();
                                    handleFilterClick('그래픽디자인');
                                }}
                            >
                                <i></i>그래픽디자인
                            </a>
                        </div>
                    </div>
                    <div className="my-main-content">
                        <h2 className="h3-title">추첨형 캠페인</h2>
                        <section className="shadowBox3">
                            <div style={{ padding: '50px', textAlign: 'center' }}>
                                <p>해당 카테고리에 캠페인이 없습니다.</p>
                            </div>
                        </section>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="outer-layout-bg">
            <div className="mypage-2col-container">
                {/* 왼쪽: InquiryForm과 완전히 동일한 사이드바 */}
                <div className="my-lnb-sidebar">
                    <dl className="user-greeting">
                        <dt>추첨형 캠페인</dt>
                        <dd>다양한 상품을 만나보세요</dd>
                    </dl>
                    <div className="code-customer-menu">
                        <a
                            href=""
                            className={activeFilter === '웹디자인' ? 'active' : ''}
                            onClick={(e) => {
                                e.preventDefault();
                                handleFilterClick('웹디자인');
                            }}
                        >
                            <i></i>웹디자인 <span style={{
                            background: '#ff6b35',
                            color: 'white',
                            borderRadius: '10px',
                            padding: '2px 6px',
                            fontSize: '12px',
                            marginLeft: '5px'
                        }}>3</span>
                        </a>
                        <a
                            href=""
                            className={activeFilter === 'UX/UI디자인' ? 'active' : ''}
                            onClick={(e) => {
                                e.preventDefault();
                                handleFilterClick('UX/UI디자인');
                            }}
                        >
                            <i></i>UX/UI디자인 <span style={{
                            background: '#ff6b35',
                            color: 'white',
                            borderRadius: '10px',
                            padding: '2px 6px',
                            fontSize: '12px',
                            marginLeft: '5px'
                        }}>2</span>
                        </a>
                        <a
                            href=""
                            className={activeFilter === '그래픽디자인' ? 'active' : ''}
                            onClick={(e) => {
                                e.preventDefault();
                                handleFilterClick('그래픽디자인');
                            }}
                        >
                            <i></i>그래픽디자인 <span style={{
                            background: '#ff6b35',
                            color: 'white',
                            borderRadius: '10px',
                            padding: '2px 6px',
                            fontSize: '12px',
                            marginLeft: '5px'
                        }}>2</span>
                        </a>
                    </div>
                </div>

                {/* 오른쪽: 메인 컨텐츠 영역 */}
                <div className="my-main-content">
                    <h2 className="h3-title">추첨형 캠페인</h2>

                    <div style={{ display: 'flex', gap: '20px', height: '75vh' }}>
                        {/* 중앙: 현재 아이템 */}
                        <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
                            <section className="shadowBox3" style={{ flex: 1, position: 'relative' }}>
                                <div className="slide-card-ty1 horizontal-card">
                                    <div className="item" style={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                                        <a href="">
                                            <div className="badge-group">
                                                <p className={`badge-pos-left ${currentItem.badge}`}>{currentItem.badgeText}</p>
                                            </div>
                                            <div className="item-photo-box" style={{
                                                flex: 1,
                                                display: 'flex',
                                                alignItems: 'center',
                                                justifyContent: 'center',
                                                minHeight: '300px'
                                            }}>
                                                <img src={currentItem.image} className="img" alt="추첨상품" />
                                            </div>
                                            <ul className="item-info-box">
                                                <li className="mall-name">{currentItem.mallName}</li>
                                                <li className="campaign-title">{currentItem.title}</li>
                                                <li className="lottery-info">
                                                    <span className="participants">참여자 수: <strong>{currentItem.participants}</strong></span>
                                                    <span className="winners">당첨자: <strong>{currentItem.winners}</strong></span>
                                                </li>
                                                <li className="deadline">{currentItem.deadline}</li>
                                            </ul>
                                        </a>
                                    </div>
                                </div>

                                {/* 네비게이션 버튼 */}
                                {currentItemIndex > 0 && (
                                    <button
                                        onClick={handlePrevItem}
                                        style={{
                                            position: 'absolute',
                                            left: '10px',
                                            top: '50%',
                                            transform: 'translateY(-50%)',
                                            background: 'rgba(0,0,0,0.6)',
                                            color: 'white',
                                            border: 'none',
                                            borderRadius: '50%',
                                            width: '45px',
                                            height: '45px',
                                            cursor: 'pointer',
                                            fontSize: '20px',
                                            display: 'flex',
                                            alignItems: 'center',
                                            justifyContent: 'center',
                                            zIndex: 10
                                        }}
                                    >
                                        ‹
                                    </button>
                                )}

                                {currentItemIndex < currentItems.length - 1 && (
                                    <button
                                        onClick={handleNextItem}
                                        style={{
                                            position: 'absolute',
                                            right: '10px',
                                            top: '50%',
                                            transform: 'translateY(-50%)',
                                            background: 'rgba(0,0,0,0.6)',
                                            color: 'white',
                                            border: 'none',
                                            borderRadius: '50%',
                                            width: '45px',
                                            height: '45px',
                                            cursor: 'pointer',
                                            fontSize: '20px',
                                            display: 'flex',
                                            alignItems: 'center',
                                            justifyContent: 'center',
                                            zIndex: 10
                                        }}
                                    >
                                        ›
                                    </button>
                                )}

                                {/* 인디케이터 */}
                                <div style={{
                                    position: 'absolute',
                                    bottom: '15px',
                                    left: '50%',
                                    transform: 'translateX(-50%)',
                                    display: 'flex',
                                    gap: '8px',
                                    zIndex: 10
                                }}>
                                    {currentItems.map((_, index) => (
                                        <div
                                            key={index}
                                            style={{
                                                width: '10px',
                                                height: '10px',
                                                borderRadius: '50%',
                                                background: index === currentItemIndex ? '#007bff' : 'rgba(255,255,255,0.7)',
                                                cursor: 'pointer',
                                                border: '1px solid rgba(0,0,0,0.2)',
                                                transition: 'all 0.3s ease'
                                            }}
                                            onClick={() => setCurrentItemIndex(index)}
                                        />
                                    ))}
                                </div>
                            </section>
                        </div>

                        {/* 오른쪽: 댓글 영역 */}
                        <div style={{ width: '350px', flexShrink: 0 }}>
                            <section className="shadowBox3" style={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                                <div style={{ padding: '20px', borderBottom: '1px solid #eee' }}>
                                    <h3 style={{ margin: 0, fontSize: '18px', fontWeight: 'bold', color: '#333' }}>
                                        댓글 <span style={{ color: '#999', fontSize: '14px' }}>
                                            ({getCommentsForItem(currentItem.id).length})
                                        </span>
                                    </h3>
                                </div>

                                {/* 댓글 목록 */}
                                <div style={{
                                    flex: 1,
                                    overflowY: 'auto',
                                    padding: '10px 20px',
                                    maxHeight: 'calc(75vh - 200px)'
                                }}>
                                    {getCommentsForItem(currentItem.id).map(comment => (
                                        <div key={comment.id} style={{
                                            display: 'flex',
                                            gap: '12px',
                                            padding: '15px 0',
                                            borderBottom: '1px solid #f8f9fa'
                                        }}>
                                            <div style={{
                                                width: '40px',
                                                height: '40px',
                                                borderRadius: '50%',
                                                background: `linear-gradient(45deg, #667eea, #764ba2)`,
                                                display: 'flex',
                                                alignItems: 'center',
                                                justifyContent: 'center',
                                                color: 'white',
                                                fontSize: '14px',
                                                fontWeight: 'bold',
                                                flexShrink: 0
                                            }}>
                                                {comment.avatar}
                                            </div>
                                            <div style={{ flex: 1, minWidth: 0 }}>
                                                <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '5px' }}>
                                                    <span style={{ fontWeight: 'bold', fontSize: '14px', color: '#333' }}>{comment.username}</span>
                                                    <span style={{ fontSize: '12px', color: '#999' }}>{comment.timeAgo}</span>
                                                </div>
                                                <p style={{ margin: 0, fontSize: '14px', lineHeight: '1.5', color: '#555', wordBreak: 'break-word' }}>{comment.content}</p>
                                            </div>
                                        </div>
                                    ))}
                                </div>

                                {/* 댓글 작성 - 하단 고정 */}
                                <div style={{
                                    padding: '20px',
                                    borderTop: '1px solid #eee',
                                    background: '#fafafa',
                                    flexShrink: 0
                                }}>
                                    <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                                        <textarea
                                            value={newComment}
                                            onChange={(e) => setNewComment(e.target.value)}
                                            placeholder="댓글을 입력하세요..."
                                            style={{
                                                width: '100%',
                                                minHeight: '80px',
                                                maxHeight: '120px',
                                                border: '1px solid #ddd',
                                                borderRadius: '8px',
                                                padding: '12px',
                                                resize: 'vertical',
                                                fontSize: '14px',
                                                background: 'white',
                                                boxSizing: 'border-box'
                                            }}
                                            onKeyPress={(e) => {
                                                if (e.key === 'Enter' && e.ctrlKey) {
                                                    handleAddComment();
                                                }
                                            }}
                                        />
                                        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                            <p style={{ margin: 0, fontSize: '12px', color: '#999' }}>
                                                Ctrl + Enter로 빠른 등록
                                            </p>
                                            <button
                                                onClick={handleAddComment}
                                                className="btnXl btnMainColor1"
                                                style={{
                                                    padding: '10px 20px',
                                                    fontSize: '14px'
                                                }}
                                                disabled={!newComment.trim()}
                                            >
                                                등록
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            </section>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default InquiryForm2;
