import React, { useState } from 'react';

function CustomerCenter() {
    const [activeMenu, setActiveMenu] = useState('notice'); // 기본값: 공지사항
    const [activeTab, setActiveTab] = useState('tab01');
    const [searchQuery, setSearchQuery] = useState('');
    const [openAccordion, setOpenAccordion] = useState(null);
    const [inquiryType, setInquiryType] = useState('0');
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [file, setFile] = useState(null);
    const [agreed, setAgreed] = useState(false);

    // FAQ 데이터
    const faqData = {
        tab01: [
            {
                id: 1,
                question: "마켓별 아이디 변경이 가능한가요?",
                answer: "제공받은 제품 및 서비스를 재판매를 시도한 내용이 적발된 경우 해당 회원은 즉시 이용 정지되어 서비스 이용이 불가하며 포인트 회수 조치될 수 있습니다."
            },
            {
                id: 2,
                question: "(구매미션) 로켓배송 상품이 품절되었나요?",
                answer: "제공받은 제품 및 서비스를 재판매를 시도한 내용이 적발된 경우 해당 회원은 즉시 이용 정지되어 서비스 이용이 불가하며 포인트 회수 조치될 수 있습니다."
            }
        ],
        tab02: [
            {
                id: 3,
                question: "서비스 이용 방법을 알려주세요",
                answer: "서비스 이용 방법에 대한 안내입니다. 회원가입 후 로그인하여 캠페인을 선택하고 참여하실 수 있습니다."
            }
        ]
    };

    // 공지사항 데이터
    const noticeData = [
        {
            id: 1,
            title: "캠페인 구매미션의 기본 자동 취소 시간 변경 안내",
            date: "2023.06.06",
            content: `공지사항 내용이 들어갑니다. 공지사항 내용이 들어갑니다. 공지사항 내용이 들어갑니다. 공지사항 내용이 들어갑니다. 공지사항 내용이 들어갑니다. 공지사항 내용이 들어갑니다.

공지사항 내용이 들어갑니다. 공지사항 내용이 들어갑니다. 공지사항 내용이 들어갑니다. 공지사항 내용이 들어갑니다. 공지사항 내용이 들어갑니다. 공지사항 내용이 들어갑니다.

https://www.google.com/search?q=%EC%BF%A0%ED%8C%A1`
        }
    ];

    const tabs = [
        { id: 'tab01', label: '전체' },
        { id: 'tab02', label: '이용안내' },
        { id: 'tab03', label: '캠페인 지원' },
        { id: 'tab04', label: '캠페인 참여' },
        { id: 'tab05', label: '캠페인 취소' },
        { id: 'tab06', label: '오류장애' },
        { id: 'tab07', label: '등급제' },
        { id: 'tab08', label: '포인트' },
        { id: 'tab09', label: '쿠폰' },
        { id: 'tab10', label: '개인정보' },
        { id: 'tab11', label: '꿀부업 제품' },
        { id: 'tab12', label: '기타' }
    ];

    const handleTabClick = (tabId) => {
        setActiveTab(tabId);
        setOpenAccordion(null);
    };

    const handleAccordionClick = (questionId) => {
        setOpenAccordion(openAccordion === questionId ? null : questionId);
    };

    const handleSearch = (e) => {
        e.preventDefault();
        console.log('검색어:', searchQuery);
    };

    const handleInquirySubmit = (e) => {
        e.preventDefault();
        if (!agreed) {
            alert('내용에 동의해주세요.');
            return;
        }
        if (!title.trim()) {
            alert('제목을 입력해주세요.');
            return;
        }
        if (!content.trim()) {
            alert('내용을 입력해주세요.');
            return;
        }

        console.log({
            inquiryType,
            title,
            content,
            file,
            agreed
        });

        alert('문의가 성공적으로 제출되었습니다.');
    };

    const handleFileChange = (e) => {
        const selectedFile = e.target.files[0];
        setFile(selectedFile);
    };

    const currentFaqData = faqData[activeTab] || [];

    // 메뉴별 콘텐츠 렌더링
    const renderContent = () => {
        switch(activeMenu) {
            case 'notice':
                return (
                    <>
                        <h2 className="h3-title">공지사항</h2>
                        <section className="shadowBox3">
                            {noticeData.map(notice => (
                                <ul key={notice.id} className="customer-table-detail">
                                    <li className="title-area">
                                        <p className="subject">{notice.title}</p>
                                        <p className="date">{notice.date}</p>
                                    </li>
                                    <li className="textbox">
                                        {notice.content.split('\n').map((line, index) => (
                                            <p key={index}>{line}</p>
                                        ))}
                                        {/* <img src="../static/images/test-04.jpg" className="img" alt="공지사항 이미지" /> */}
                                    </li>
                                </ul>
                            ))}
                        </section>
                    </>
                );

            case 'faq':
                return (
                    <>
                        <h2 className="h3-title">자주하는 질문</h2>
                        <div className="top-search-pos-right">
                            <div className="search-fieldset">
                                <form onSubmit={handleSearch}>
                                    <input
                                        type="text"
                                        name="search"
                                        id="search"
                                        value={searchQuery}
                                        onChange={(e) => setSearchQuery(e.target.value)}
                                        placeholder="검색어 입력"
                                        className="inputBox-round"
                                    />
                                    <button type="submit" className="btn-search" aria-label="검색"></button>
                                </form>
                            </div>
                        </div>
                        <div className="tab-type-tab-shadow">
                            <ul className="tab-content">
                                {tabs.map(tab => (
                                    <li key={tab.id} className={activeTab === tab.id ? 'active' : ''}>
                                        <a
                                            href={`#${tab.id}`}
                                            onClick={(e) => {
                                                e.preventDefault();
                                                handleTabClick(tab.id);
                                            }}
                                        >
                                            {tab.label}
                                        </a>
                                    </li>
                                ))}
                            </ul>
                            <div className="tabcontent top">
                                <div id={activeTab}>
                                    <section className="shadowBox3">
                                        <div className="faq-accordion-caution">
                                            {currentFaqData.length > 0 ? (
                                                currentFaqData.map(faq => (
                                                    <div key={faq.id}>
                                                        <button
                                                            className={`acc-title ${openAccordion === faq.id ? 'active' : ''}`}
                                                            onClick={() => handleAccordionClick(faq.id)}
                                                        >
                                                            {faq.question}
                                                        </button>
                                                        <div
                                                            className={`acc-content ${openAccordion === faq.id ? 'open' : ''}`}
                                                            style={{
                                                                display: openAccordion === faq.id ? 'block' : 'none'
                                                            }}
                                                        >
                                                            <p className="in-text">
                                                                {faq.answer}
                                                            </p>
                                                        </div>
                                                    </div>
                                                ))
                                            ) : (
                                                <div className="no-data">
                                                    <p>해당 카테고리에 등록된 FAQ가 없습니다.</p>
                                                </div>
                                            )}
                                        </div>
                                    </section>
                                </div>
                            </div>
                        </div>
                    </>
                );

            case 'inquiry':
                return (
                    <>
                        <h2 className="h3-title">1:1 문의하기</h2>
                        <section className="shadowBox3">
                            <ul className="my-head-notice">
                                <li className="pos-left">
                                    <ul className="tab-style-round">
                                        <li className="active">
                                            <a href="">1:1문의하기</a>
                                        </li>
                                        <li>
                                            <a href="">문의확인리스트</a>
                                        </li>
                                    </ul>
                                </li>
                            </ul>
                        </section>
                        <form onSubmit={handleInquirySubmit}>
                            <section className="sec-customer">
                                <ul className="panel-input-col mt30">
                                    <div className="flex">
                                        <li className="row-item">
                                            <span className="subject">문의유형</span>
                                            <div className="conbox col-3">
                                                <div className="select-default left">
                                                    <select
                                                        name="inquiryType"
                                                        id="inquiryType"
                                                        value={inquiryType}
                                                        onChange={(e) => setInquiryType(e.target.value)}
                                                    >
                                                        <option value="0">전체</option>
                                                        <option value="usage">이용안내</option>
                                                        <option value="campaign_support">캠페인 지원</option>
                                                        <option value="campaign_participation">캠페인 참여</option>
                                                        <option value="campaign_cancel">캠페인 취소</option>
                                                        <option value="error">오류장애</option>
                                                        <option value="grade">등급제</option>
                                                        <option value="point">포인트</option>
                                                        <option value="coupon">쿠폰</option>
                                                        <option value="privacy">개인정보</option>
                                                        <option value="product">꿀부업 제품</option>
                                                        <option value="etc">기타</option>
                                                    </select>
                                                </div>
                                            </div>
                                        </li>
                                        <li className="row-item">
                                            <span className="subject">제목</span>
                                            <div className="conbox left">
                                                <input
                                                    type="text"
                                                    name="title"
                                                    id="title"
                                                    value={title}
                                                    onChange={(e) => setTitle(e.target.value)}
                                                    placeholder="제목을 입력해주세요"
                                                />
                                            </div>
                                        </li>
                                    </div>
                                </ul>
                                <ul className="panel-input-col mt30">
                                    <li className="row-item">
                                        <span className="subject">내용 작성</span>
                                        <textarea
                                            name="content"
                                            value={content}
                                            onChange={(e) => setContent(e.target.value)}
                                            placeholder="내용을 작성해주세요"
                                        />
                                    </li>
                                    <li className="row-item filebox">
                                        <span className="subject">첨부파일</span>
                                        <label htmlFor="file">
                                            <div className="btn-upload">
                                                파일 업로드하기
                                                {file && <span> - {file.name}</span>}
                                            </div>
                                        </label>
                                        <input
                                            type="file"
                                            name="file"
                                            id="file"
                                            onChange={handleFileChange}
                                            style={{ display: 'none' }}
                                        />
                                    </li>
                                </ul>
                                <ul className="auto-id-save auto-center">
                                    <li className="auto-center">
                                        <input
                                            type="checkbox"
                                            id="autologin"
                                            name="auto"
                                            checked={agreed}
                                            onChange={(e) => setAgreed(e.target.checked)}
                                        />
                                        <label htmlFor="autologin" className="checkBox">
                                            <span></span>
                                            <em className="txt">위 내용에 동의합니다</em>
                                        </label>
                                    </li>
                                </ul>
                                <div className="bwhalf mt40">
                                    <button type="submit" className="btnXl btnMainColor1">
                                        제출하기
                                    </button>
                                </div>
                            </section>
                        </form>
                    </>
                );

            default:
                return null;
        }
    };

    return (
        <div className="outer-layout-bg">
            <div className="mypage-2col-container">
                {/* 사이드바 메뉴 */}
                <div className="my-lnb-sidebar">
                    <dl className="user-greeting">
                        <dt>꿀부업 고객센터</dt>
                        <dd>무엇이든지 물어보세요</dd>
                    </dl>
                    <div className="code-customer-menu">
                        <a
                            href="#notice"
                            className={activeMenu === 'notice' ? 'active' : ''}
                            onClick={(e) => {
                                e.preventDefault();
                                setActiveMenu('notice');
                            }}
                        >
                            <i></i>공지사항
                        </a>
                        <a
                            href="#faq"
                            className={activeMenu === 'faq' ? 'active' : ''}
                            onClick={(e) => {
                                e.preventDefault();
                                setActiveMenu('faq');
                            }}
                        >
                            <i></i>자주하는 질문
                        </a>
                        <a
                            href="#inquiry"
                            className={activeMenu === 'inquiry' ? 'active' : ''}
                            onClick={(e) => {
                                e.preventDefault();
                                setActiveMenu('inquiry');
                            }}
                        >
                            <i></i>1:1문의
                        </a>
                    </div>
                </div>

                {/* 메인 콘텐츠 */}
                <div className="my-main-content">
                    {renderContent()}
                </div>
            </div>
        </div>
    );
}

export default CustomerCenter;
