import React, { useState } from 'react';
import GnbHeader from './GnbHeader'; // 헤더 컴포넌트 import
import InquiryForm from '../routes/InquiryForm'; // InquiryForm 컴포넌트 import
import InquiryForm2 from '../routes/InquiryForm2'; // InquiryForm2 컴포넌트 import

function App() {
  const [activeTab, setActiveTab] = useState('홈');

  const handleTabClick = (tabName) => {
    setActiveTab(tabName);
  };

  const renderContent = () => {
    switch(activeTab) {
      case '홈':
        return <InquiryForm />;

      case '추첨형':
        return <InquiryForm2 />;

      case '즉석 추첨형':
        return <div>즉석 추첨형 페이지</div>;

      default:
        return null;
    }
  };

  return (
      <div className="app-container">
        <GnbHeader activeTab={activeTab} onTabClick={handleTabClick} />

        <main>
          {renderContent()}
        </main>
      </div>
  );
}

export default App;
