import React, { useState, useEffect } from 'react'; // useEffect 추가!
// JSON 파일 import 제거 (더 이상 사용하지 않음)
// import portfolioFaq from '../data/portfolioFaq.json';

// 공지사항 컴포넌트 (DB 연동으로 수정 필요 - 나중에)
function NoticeSection() {
    const [selectedNotice, setSelectedNotice] = useState(null);
    // 임시로 하드코딩된 데이터 사용 (나중에 DB 연동)
    const noticeData = [
        {
            id: 1,
            title: "포트폴리오 업데이트 안내",
            date: "2024.12.15",
            content: "새로운 프로젝트들이 포트폴리오에 추가되었습니다.\n\n최근 진행한 UX/UI 리뉴얼 프로젝트와 서비스 디자인 케이스 스터디를 확인해보세요.\n\n더 자세한 내용은 개별 프로젝트 페이지에서 확인할 수 있습니다."
        },
        {
            id: 2,
            title: "디자인 시스템 케이스 스터디 공개",
            date: "2024.11.28",
            content: "스타트업에서 처음부터 구축한 디자인 시스템 케이스 스터디를 공개했습니다.\n\nFigma와 Storybook을 활용한 개발 연동 과정과 팀 협업 개선 사례를 상세히 다뤘습니다."
        }
    ];

    const handleNoticeClick = (noticeId) => {
        setSelectedNotice(selectedNotice === noticeId ? null : noticeId);
    };

    const handleBackToList = () => {
        setSelectedNotice(null);
    };

    if (selectedNotice) {
        const notice = noticeData.find(n => n.id === selectedNotice);
        return (
            <>
                <h2 className="h3-title">공지사항</h2>
                <section className="shadowBox3">
                    <ul className="customer-table-detail">
                        <li className="title-area">
                            <p className="subject">{notice.title}</p>
                            <p className="date">{notice.date}</p>
                        </li>
                        <li className="textbox">
                            {notice.content.split('\n').map((line, index) => (
                                <React.Fragment key={index}>
                                    {line}
                                    {index < notice.content.split('\n').length - 1 && <><br/><br/></>}
                                </React.Fragment>
                            ))}
                            <br/><br/>
                            <img src="../static/images/test-04.jpg" className="img" alt="공지사항 이미지" />
                        </li>
                    </ul>
                    <div style={{ marginTop: '20px', textAlign: 'center' }}>
                        <button
                            type="button"
                            className="btnXl btnMainColor1"
                            onClick={handleBackToList}
                        >
                            목록으로 돌아가기
                        </button>
                    </div>
                </section>
            </>
        );
    }

    return (
        <>
            <h2 className="h3-title">공지사항</h2>
            <section className="shadowBox3">
                <ul className="customer-table-list">
                    {noticeData.map(notice => (
                        <li key={notice.id} className="item">
                            <a
                                href=""
                                onClick={(e) => {
                                    e.preventDefault();
                                    handleNoticeClick(notice.id);
                                }}
                            >
                                <p className="subject">
                                    <span>{notice.title}</span>
                                    <i className="more"></i>
                                </p>
                                <p className="date">{notice.date}</p>
                            </a>
                        </li>
                    ))}
                </ul>
            </section>
        </>
    );
}

// FAQ 컴포넌트 (DB 연동 버전)
function FaqSection() {
    const [activeTab, setActiveTab] = useState('디자인 프로세스');
    const [searchQuery, setSearchQuery] = useState('');
    const [openAccordions, setOpenAccordions] = useState(new Set());

    // DB에서 가져올 데이터
    const [faqData, setFaqData] = useState([]);
    const [categories, setCategories] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchResults, setSearchResults] = useState([]);
    const [isSearchMode, setIsSearchMode] = useState(false);

    // 컴포넌트 마운트 시 카테고리와 FAQ 데이터 가져오기
    useEffect(() => {
        fetchCategories();
        fetchFaqData();
    }, []);

    // 카테고리 목록 가져오기
    const fetchCategories = async () => {
        try {
            const response = await fetch('/api/faq/categories');
            const data = await response.json();
            if (data.success) {
                setCategories(data.data);
                if (data.data.length > 0) {
                    setActiveTab(data.data[0]); // 첫 번째 카테고리를 기본값으로
                }
            }
        } catch (error) {
            console.error('카테고리 로딩 오류:', error);
        }
    };

    // FAQ 데이터 가져오기 (카테고리별)
    const fetchFaqData = async (category = null) => {
        try {
            setLoading(true);
            const url = category ? `/api/faq?category=${encodeURIComponent(category)}` : '/api/faq';
            const response = await fetch(url);
            const data = await response.json();

            if (data.success) {
                setFaqData(data.data);
            }
        } catch (error) {
            console.error('FAQ 데이터 로딩 오류:', error);
        } finally {
            setLoading(false);
        }
    };

    // 검색 기능
    const handleSearch = async (e) => {
        e.preventDefault();

        if (!searchQuery.trim()) {
            // 검색어가 없으면 일반 모드로 돌아가기
            setIsSearchMode(false);
            fetchFaqData(activeTab);
            return;
        }

        try {
            setLoading(true);
            const response = await fetch(`/api/faq/search?q=${encodeURIComponent(searchQuery)}`);
            const data = await response.json();

            if (data.success) {
                setSearchResults(data.data);
                setIsSearchMode(true);
                setOpenAccordions(new Set()); // 검색 시 모든 아코디언 닫기
            }
        } catch (error) {
            console.error('검색 오류:', error);
        } finally {
            setLoading(false);
        }
    };

    // 탭 클릭 핸들러
    const handleTabClick = (category) => {
        setActiveTab(category);
        setOpenAccordions(new Set());
        setIsSearchMode(false);
        setSearchQuery(''); // 검색어 초기화
        fetchFaqData(category);
    };

    // 아코디언 클릭 핸들러
    const handleAccordionClick = (questionId) => {
        const newOpenAccordions = new Set(openAccordions);
        if (newOpenAccordions.has(questionId)) {
            newOpenAccordions.delete(questionId);
        } else {
            newOpenAccordions.add(questionId);
        }
        setOpenAccordions(newOpenAccordions);
    };

    // 검색어 변경 시 실시간 검색 (선택사항)
    const handleSearchInputChange = (e) => {
        const value = e.target.value;
        setSearchQuery(value);

        // 검색어가 비어있으면 일반 모드로 돌아가기
        if (!value.trim()) {
            setIsSearchMode(false);
            fetchFaqData(activeTab);
        }
    };

    // 현재 표시할 데이터 결정
    const currentFaqData = isSearchMode ? searchResults : faqData;

    return (
        <>
            <h2 className="h3-title">자주하는 질문</h2>
            <div className="top-search-pos-right">
                <div className="search-fieldset">
                    <input
                        type="text"
                        name="search"
                        id="search"
                        value={searchQuery}
                        onChange={handleSearchInputChange}
                        placeholder="검색어 입력"
                        className="inputBox-round"
                    />
                    <button
                        type="button"
                        className="btn-search"
                        onClick={handleSearch}
                        aria-label="검색"
                    ></button>
                </div>
            </div>

            {/* 검색 모드일 때 안내 메시지 */}
            {isSearchMode && (
                <div className="search-info">
                    <p>"{searchQuery}" 검색 결과: {searchResults.length}개</p>
                    <button
                        onClick={() => {
                            setIsSearchMode(false);
                            setSearchQuery('');
                            fetchFaqData(activeTab);
                        }}
                        className="btn-clear-search"
                    >
                        전체 보기
                    </button>
                </div>
            )}

            <div className="tab-type-tab-shadow">
                {/* 검색 모드가 아닐 때만 탭 표시 */}
                {!isSearchMode && (
                    <ul className="tab-content">
                        {categories.map(category => (
                            <li key={category} className={activeTab === category ? 'active' : ''}>
                                <a
                                    href={`#${category}`}
                                    onClick={(e) => {
                                        e.preventDefault();
                                        handleTabClick(category);
                                    }}
                                >
                                    {category}
                                </a>
                            </li>
                        ))}
                    </ul>
                )}

                <div className="tabcontent top">
                    <div id={activeTab}>
                        <section className="shadowBox3">
                            <div className="faq-accordion-caution">
                                {loading ? (
                                    <div className="loading">
                                        <p>로딩 중...</p>
                                    </div>
                                ) : currentFaqData.length > 0 ? (
                                    currentFaqData.map(faq => (
                                        <div key={faq.id}>
                                            <button
                                                className={`acc-title ${openAccordions.has(faq.id) ? 'active' : ''}`}
                                                onClick={() => handleAccordionClick(faq.id)}
                                            >
                                                {/* 검색 모드일 때 카테고리 표시 */}
                                                {isSearchMode && (
                                                    <span className="category-badge">[{faq.category}]</span>
                                                )}
                                                {faq.question}
                                            </button>
                                            <div
                                                className="acc-content"
                                                style={{
                                                    maxHeight: openAccordions.has(faq.id) ? 'none' : '0',
                                                    overflow: 'hidden',
                                                    transition: 'max-height 0.3s ease'
                                                }}
                                            >
                                                <p className="in-text">{faq.answer}</p>
                                            </div>
                                        </div>
                                    ))
                                ) : (
                                    <div className="no-data">
                                        <p>
                                            {isSearchMode
                                                ? `"${searchQuery}"에 대한 검색 결과가 없습니다.`
                                                : '해당 카테고리에 등록된 FAQ가 없습니다.'
                                            }
                                        </p>
                                    </div>
                                )}
                            </div>
                        </section>
                    </div>
                </div>
            </div>
        </>
    );
}

