// 1:1 문의 컴포넌트 (수정된 탭 구조)
function InquirySection() {
    const [activeInquiryTab, setActiveInquiryTab] = useState('form'); // 'form' 또는 'list'
    const [inquiryType, setInquiryType] = useState('0');
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [file, setFile] = useState(null);
    const [agreed, setAgreed] = useState(false);

    const handleInquirySubmit = (e) => {
        e.preventDefault();

        if (!agreed) {
            alert('내용에 동의해주세요.');
            return;
        }
        if (!title.trim()) {
            alert('제목을 입력해주세요.');
            return;
        }
        if (!content.trim()) {
            alert('내용을 입력해주세요.');
            return;
        }

        console.log({ inquiryType, title, content, file, agreed });
        alert('문의가 성공적으로 제출되었습니다.');
    };

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    if (activeInquiryTab === 'list') {
        return <InquiryListSection />;
    }

    return (
        <>
            <h2 className="h3-title">1:1 문의하기</h2>
            <section className="shadowBox3">
                <ul className="my-head-notice">
                    <li className="pos-left">
                        <ul className="tab-style-round">
                            <li className={activeInquiryTab === 'form' ? 'active' : ''}>
                                <a
                                    href=""
                                    onClick={(e) => {
                                        e.preventDefault();
                                        setActiveInquiryTab('form');
                                    }}
                                >
                                    1:1문의하기
                                </a>
                            </li>
                            <li className={activeInquiryTab === 'list' ? 'active' : ''}>
                                <a
                                    href=""
                                    onClick={(e) => {
                                        e.preventDefault();
                                        setActiveInquiryTab('list');
                                    }}
                                >
                                    문의확인리스트
                                </a>
                            </li>
                        </ul>
                    </li>
                </ul>
            </section>
            <div>
                <section className="sec-customer">
                    <ul className="panel-input-col mt30">
                        <div className="flex">
                            <li className="row-item">
                                <span className="subject">문의유형</span>
                                <div className="conbox col-3">
                                    <div className="select-default left">
                                        <select
                                            name="inquiryType"
                                            id="inquiryType"
                                            value={inquiryType}
                                            onChange={(e) => setInquiryType(e.target.value)}
                                        >
                                            <option value="0">전체</option>
                                            <option value="usage">이용안내</option>
                                            <option value="campaign_support">캠페인 지원</option>
                                            <option value="campaign_participation">캠페인 참여</option>
                                            <option value="campaign_cancel">캠페인 취소</option>
                                            <option value="error">오류장애</option>
                                            <option value="grade">등급제</option>
                                            <option value="point">포인트</option>
                                            <option value="coupon">쿠폰</option>
                                            <option value="privacy">개인정보</option>
                                            <option value="product">꿀부업 제품</option>
                                            <option value="etc">기타</option>
                                        </select>
                                    </div>
                                </div>
                            </li>
                            <li className="row-item">
                                <span className="subject">제목</span>
                                <div className="conbox left">
                                    <input
                                        type="text"
                                        name="title"
                                        id="title"
                                        value={title}
                                        onChange={(e) => setTitle(e.target.value)}
                                        placeholder="제목을 입력해주세요"
                                    />
                                </div>
                            </li>
                        </div>
                    </ul>
                    <ul className="panel-input-col mt30">
                        <li className="row-item">
                            <span className="subject">내용 작성</span>
                            <textarea
                                name="content"
                                value={content}
                                onChange={(e) => setContent(e.target.value)}
                                placeholder="내용을 작성해주세요"
                            />
                        </li>
                        <li className="row-item filebox">
                            <span className="subject">첨부파일</span>
                            <label htmlFor="file">
                                <div className="btn-upload">
                                    파일 업로드하기
                                    {file && <span> - {file.name}</span>}
                                </div>
                            </label>
                            <input
                                type="file"
                                name="file"
                                id="file"
                                onChange={handleFileChange}
                                style={{ display: 'none' }}
                            />
                        </li>
                    </ul>
                    <ul className="auto-id-save auto-center">
                        <li className="auto-center">
                            <input
                                type="checkbox"
                                id="autologin"
                                name="auto"
                                checked={agreed}
                                onChange={(e) => setAgreed(e.target.checked)}
                            />
                            <label htmlFor="autologin" className="checkBox">
                                <span></span>
                                <em className="txt">위 내용에 동의합니다</em>
                            </label>
                        </li>
                    </ul>
                    <div className="bwhalf mt40">
                        <button type="button" className="btnXl btnMainColor1" onClick={handleInquirySubmit}>
                            제출하기
                        </button>
                    </div>
                </section>
            </div>
        </>
    );
}

// 사이드바 컴포넌트 (원본 HTML 구조와 일치)
function Sidebar({ activeMenu, setActiveMenu }) {
    const menuItems = [
        { id: 'notice', label: '업데이트 소식', active: activeMenu === 'notice' },
        { id: 'faq', label: '자주하는 질문', active: activeMenu === 'faq' },
        { id: 'inquiry', label: '문의하기', active: activeMenu === 'inquiry' }
    ];

    return (
        <div className="my-lnb-sidebar">
            <dl className="user-greeting">
                <dt>포트폴리오 FAQ</dt>
                <dd>궁금한 것을 찾아보세요</dd>
            </dl>
            <div className="code-customer-menu">
                {menuItems.map(item => (
                    <a
                        key={item.id}
                        href=""
                        className={item.active ? 'active' : ''}
                        onClick={(e) => {
                            e.preventDefault();
                            setActiveMenu(item.id);
                        }}
                    >
                        <i></i>{item.label}
                    </a>
                ))}
            </div>
        </div>
    );
}

// InquiryForm = 고객센터 컴포넌트 (연회색 배경이 100% 차지)
function InquiryForm() {
    const [activeMenu, setActiveMenu] = useState('notice');

    const renderContent = () => {
        switch(activeMenu) {
            case 'notice':
                return <NoticeSection />;
            case 'faq':
                return <FaqSection />;
            case 'inquiry':
                return <InquirySection />;
            default:
                return <NoticeSection />;
        }
    };

    return (
        <div className="outer-layout-bg">
            <div className="mypage-2col-container">
                <Sidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} />
                <div className="my-main-content">
                    {renderContent()}
                </div>
            </div>
        </div>
    );
}

export default InquiryForm;
