import React, { useState } from 'react';
// JSON 파일 import (src/data/portfolioFaq.json)
import portfolioFaq from '../data/portfolioFaq.json';

// 공지사항 컴포넌트 (아코디언 기능 추가)
function NoticeSection() {
    const [selectedNotice, setSelectedNotice] = useState(null);

    const handleNoticeClick = (noticeId) => {
        setSelectedNotice(selectedNotice === noticeId ? null : noticeId);
    };

    const handleBackToList = () => {
        setSelectedNotice(null);
    };

    if (selectedNotice) {
        const notice = portfolioFaq.notice.find(n => n.id === selectedNotice);
        return (
            <>
                <h2 className="h3-title">업데이트 소식</h2>
                <section className="shadowBox3">
                    <ul className="customer-table-detail">
                        <li className="title-area">
                            <p className="subject">{notice.title}</p>
                            <p className="date">{notice.date}</p>
                        </li>
                        <li className="textbox">
                            {notice.content.split('\n').map((line, index) => (
                                <React.Fragment key={index}>
                                    {line}
                                    {index < notice.content.split('\n').length - 1 && <><br/><br/></>}
                                </React.Fragment>
                            ))}
                            <br/><br/>
                            <img src="../static/images/test-04.jpg" className="img" alt="공지사항 이미지" />
                        </li>
                    </ul>
                    <div style={{ marginTop: '20px', textAlign: 'center' }}>
                        <button
                            type="button"
                            className="btnXl btnMainColor1"
                            onClick={handleBackToList}
                        >
                            목록으로 돌아가기
                        </button>
                    </div>
                </section>
            </>
        );
    }

    return (
        <>
            <h2 className="h3-title">업데이트 소식</h2>
            <section className="shadowBox3">
                <ul className="customer-table-list">
                    {portfolioFaq.notice.map(notice => (
                        <li key={notice.id} className="item">
                            <a
                                href=""
                                onClick={(e) => {
                                    e.preventDefault();
                                    handleNoticeClick(notice.id);
                                }}
                            >
                                <p className="subject">
                                    <span>{notice.title}</span>
                                    <i className="more"></i>
                                </p>
                                <p className="date">{notice.date}</p>
                            </a>
                        </li>
                    ))}
                </ul>
            </section>
        </>
    );
}

// FAQ 컴포넌트 (원본 HTML 아코디언 구조 적용)
function FaqSection() {
    const [activeTab, setActiveTab] = useState('tab01');
    const [searchQuery, setSearchQuery] = useState('');
    const [openAccordions, setOpenAccordions] = useState(new Set());

    const handleTabClick = (tabId) => {
        setActiveTab(tabId);
        setOpenAccordions(new Set());
    };

    const handleAccordionClick = (questionId) => {
        const newOpenAccordions = new Set(openAccordions);
        if (newOpenAccordions.has(questionId)) {
            newOpenAccordions.delete(questionId);
        } else {
            newOpenAccordions.add(questionId);
        }
        setOpenAccordions(newOpenAccordions);
    };

    const handleSearch = (e) => {
        e.preventDefault();
        console.log('검색어:', searchQuery);
    };

    const currentFaqData = portfolioFaq.faq[activeTab] || [];

    return (
        <>
            <h2 className="h3-title">자주하는 질문</h2>
            <div className="top-search-pos-right">
                <div className="search-fieldset">
                    <input
                        type="text"
                        name="search"
                        id="search"
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        placeholder="검색어 입력"
                        className="inputBox-round"
                    />
                    <button type="button" className="btn-search" onClick={handleSearch} aria-label="검색"></button>
                </div>
            </div>
            <div className="tab-type-tab-shadow">
                <ul className="tab-content">
                    {portfolioFaq.tabs.map(tab => (
                        <li key={tab.id} className={activeTab === tab.id ? 'active' : ''}>
                            <a
                                href={`#${tab.id}`}
                                onClick={(e) => {
                                    e.preventDefault();
                                    handleTabClick(tab.id);
                                }}
                            >
                                {tab.label}
                            </a>
                        </li>
                    ))}
                </ul>
                <div className="tabcontent top">
                    <div id={activeTab}>
                        <section className="shadowBox3">
                            <div className="faq-accordion-caution">
                                {currentFaqData.length > 0 ? (
                                    currentFaqData.map(faq => (
                                        <div key={faq.id}>
                                            <button
                                                className={`acc-title ${openAccordions.has(faq.id) ? 'active' : ''}`}
                                                onClick={() => handleAccordionClick(faq.id)}
                                            >
                                                {faq.question}
                                            </button>
                                            <div
                                                className="acc-content"
                                                style={{
                                                    maxHeight: openAccordions.has(faq.id) ? 'none' : '0',
                                                    overflow: 'hidden',
                                                    transition: 'max-height 0.3s ease'
                                                }}
                                            >
                                                <p className="in-text">{faq.answer}</p>
                                            </div>
                                        </div>
                                    ))
                                ) : (
                                    <div className="no-data">
                                        <p>해당 카테고리에 등록된 FAQ가 없습니다.</p>
                                    </div>
                                )}
                            </div>
                        </section>
                    </div>
                </div>
            </div>
        </>
    );
}

// 문의 조회 컴포넌트 (새로 추가)
function InquirySearchSection() {
    const [searchData, setSearchData] = useState({
        inquiryNumber: '',
        email: ''
    });
    const [searchResult, setSearchResult] = useState(null);
    const [isSearching, setIsSearching] = useState(false);

    // 임시 문의 데이터 (실제로는 API에서 가져올 데이터)
    const mockInquiries = [
        {
            id: 'INQ20250630001',
            email: 'user@example.com',
            date: '2025.06.30',
            title: '포트폴리오 업로드 관련 문의',
            content: '포트폴리오를 업로드할 때 이미지 크기 제한이 있나요?',
            status: '답변완료',
            answer: '이미지는 최대 10MB까지 업로드 가능하며, JPG, PNG 형식을 지원합니다.',
            answerDate: '2025.06.30'
        },
        {
            id: 'INQ20250629002',
            email: 'test@test.com',
            date: '2025.06.29',
            title: '회원가입 오류',
            content: '회원가입 시 인증번호가 오지 않습니다.',
            status: '처리중',
            answer: null,
            answerDate: null
        }
    ];

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setSearchData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSearch = (e) => {
        e.preventDefault();

        if (!searchData.inquiryNumber.trim()) {
            alert('문의번호를 입력해주세요.');
            return;
        }
        if (!searchData.email.trim()) {
            alert('이메일을 입력해주세요.');
            return;
        }

        setIsSearching(true);

        // 실제로는 API 호출
        setTimeout(() => {
            const result = mockInquiries.find(
                inquiry => inquiry.id === searchData.inquiryNumber &&
                    inquiry.email === searchData.email
            );

            setSearchResult(result || 'not_found');
            setIsSearching(false);
        }, 1000);
    };

    const resetSearch = () => {
        setSearchResult(null);
        setSearchData({ inquiryNumber: '', email: '' });
    };

    return (
        <>
            <h2 className="h3-title">내 문의 조회</h2>

            {!searchResult && (
                <section className="shadowBox3">
                    <div className="inquiry-search-form">
                        <ul className="panel-input-col">
                            <li className="row-item">
                                <span className="subject">문의번호</span>
                                <div className="conbox">
                                    <input
                                        type="text"
                                        name="inquiryNumber"
                                        value={searchData.inquiryNumber}
                                        onChange={handleInputChange}
                                        placeholder="문의번호를 입력하세요 (예: INQ20250630001)"
                                    />
                                </div>
                            </li>
                            <li className="row-item">
                                <span className="subject">이메일</span>
                                <div className="conbox">
                                    <input
                                        type="email"
                                        name="email"
                                        value={searchData.email}
                                        onChange={handleInputChange}
                                        placeholder="문의 시 입력한 이메일을 입력하세요"
                                    />
                                </div>
                            </li>
                        </ul>
                        <div className="bwhalf mt30">
                            <button
                                type="button"
                                className="btnXl btnMainColor1"
                                onClick={handleSearch}
                                disabled={isSearching}
                            >
                                {isSearching ? '조회중...' : '문의 조회'}
                            </button>
                        </div>
                    </div>
                </section>
            )}

            {searchResult === 'not_found' && (
                <section className="shadowBox3">
                    <div className="no-data" style={{ textAlign: 'center', padding: '40px 20px' }}>
                        <p>입력하신 정보와 일치하는 문의를 찾을 수 없습니다.</p>
                        <p style={{ fontSize: '14px', color: '#666', marginTop: '10px' }}>
                            문의번호와 이메일을 다시 확인해주세요.
                        </p>
                        <button
                            type="button"
                            className="btnM btnSubColor1"
                            onClick={resetSearch}
                            style={{ marginTop: '20px' }}
                        >
                            다시 조회하기
                        </button>
                    </div>
                </section>
            )}

            {searchResult && searchResult !== 'not_found' && (
                <section className="shadowBox3">
                    <ul className="customer-table-detail">
                        <li className="title-area">
                            <p className="subject">{searchResult.title}</p>
                            <div className="inquiry-meta">
                                <span className="date">문의일: {searchResult.date}</span>
                                <span className={`status ${searchResult.status === '답변완료' ? 'completed' : 'pending'}`}>
                                    {searchResult.status}
                                </span>
                            </div>
                        </li>
                        <li className="textbox">
                            <div className="inquiry-content">
                                <h4>문의 내용:</h4>
                                <p>{searchResult.content}</p>
                            </div>

                            {searchResult.answer && (
                                <div className="answer-content" style={{ marginTop: '30px', padding: '20px', backgroundColor: '#f8f9fa', borderRadius: '8px' }}>
                                    <h4 style={{ color: '#2c5aa0' }}>답변:</h4>
                                    <p>{searchResult.answer}</p>
                                    <p style={{ fontSize: '12px', color: '#666', marginTop: '10px' }}>
                                        답변일: {searchResult.answerDate}
                                    </p>
                                </div>
                            )}
                        </li>
                    </ul>
                    <div style={{ marginTop: '20px', textAlign: 'center' }}>
                        <button
                            type="button"
                            className="btnM btnSubColor1"
                            onClick={resetSearch}
                        >
                            다른 문의 조회하기
                        </button>
                    </div>
                </section>
            )}
        </>
    );
}

// 1:1 문의 폼 컴포넌트
function InquiryFormSection() {
    const [inquiryType, setInquiryType] = useState('0');
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [file, setFile] = useState(null);
    const [agreed, setAgreed] = useState(false);

    const handleInquirySubmit = (e) => {
        e.preventDefault();

        // 유효성 검사
        if (!name.trim()) {
            alert('이름을 입력해주세요.');
            return;
        }
        if (!email.trim()) {
            alert('이메일을 입력해주세요.');
            return;
        }
        if (!title.trim()) {
            alert('제목을 입력해주세요.');
            return;
        }
        if (!content.trim()) {
            alert('내용을 입력해주세요.');
            return;
        }
        if (!agreed) {
            alert('개인정보 수집 및 이용에 동의해주세요.');
            return;
        }

        // 문의번호 생성 (실제로는 서버에서 생성)
        const inquiryNumber = `INQ${new Date().getFullYear()}${(new Date().getMonth() + 1).toString().padStart(2, '0')}${new Date().getDate().toString().padStart(2, '0')}${String(Math.floor(Math.random() * 1000)).padStart(3, '0')}`;

        console.log({
            inquiryNumber,
            inquiryType,
            name,
            email,
            phone,
            title,
            content,
            file,
            agreed
        });

        alert(`문의가 성공적으로 접수되었습니다.\n\n문의번호: ${inquiryNumber}\n\n문의번호와 이메일로 답변을 확인하실 수 있습니다.`);

        // 폼 초기화
        setInquiryType('0');
        setName('');
        setEmail('');
        setPhone('');
        setTitle('');
        setContent('');
        setFile(null);
        setAgreed(false);
    };

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    return (
        <>
            <h2 className="h3-title">1:1 문의하기</h2>
            <section className="sec-customer">
                <ul className="panel-input-col mt30">
                    <div className="flex">
                        <li className="row-item">
                            <span className="subject">문의유형</span>
                            <div className="conbox col-3">
                                <div className="select-default left">
                                    <select
                                        name="inquiryType"
                                        id="inquiryType"
                                        value={inquiryType}
                                        onChange={(e) => setInquiryType(e.target.value)}
                                    >
                                        <option value="0">선택해주세요</option>
                                        <option value="portfolio">포트폴리오 관련</option>
                                        <option value="account">계정/회원가입</option>
                                        <option value="technical">기술적 문제</option>
                                        <option value="suggestion">개선 제안</option>
                                        <option value="etc">기타</option>
                                    </select>
                                </div>
                            </div>
                        </li>
                        <li className="row-item">
                            <span className="subject">이름 *</span>
                            <div className="conbox left">
                                <input
                                    type="text"
                                    name="name"
                                    id="name"
                                    value={name}
                                    onChange={(e) => setName(e.target.value)}
                                    placeholder="이름을 입력해주세요"
                                />
                            </div>
                        </li>
                    </div>
                </ul>
                <ul className="panel-input-col mt30">
                    <div className="flex">
                        <li className="row-item">
                            <span className="subject">이메일 *</span>
                            <div className="conbox left">
                                <input
                                    type="email"
                                    name="email"
                                    id="email"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    placeholder="답변받을 이메일을 입력해주세요"
                                />
                            </div>
                        </li>
                        <li className="row-item">
                            <span className="subject">연락처</span>
                            <div className="conbox left">
                                <input
                                    type="tel"
                                    name="phone"
                                    id="phone"
                                    value={phone}
                                    onChange={(e) => setPhone(e.target.value)}
                                    placeholder="연락처를 입력해주세요 (선택)"
                                />
                            </div>
                        </li>
                    </div>
                </ul>
                <ul className="panel-input-col mt30">
                    <li className="row-item">
                        <span className="subject">제목 *</span>
                        <div className="conbox left">
                            <input
                                type="text"
                                name="title"
                                id="title"
                                value={title}
                                onChange={(e) => setTitle(e.target.value)}
                                placeholder="문의 제목을 입력해주세요"
                            />
                        </div>
                    </li>
                </ul>
                <ul className="panel-input-col mt30">
                    <li className="row-item">
                        <span className="subject">내용 작성 *</span>
                        <textarea
                            name="content"
                            value={content}
                            onChange={(e) => setContent(e.target.value)}
                            placeholder="문의하실 내용을 자세히 작성해주세요"
                            rows="8"
                        />
                    </li>
                    <li className="row-item filebox">
                        <span className="subject">첨부파일</span>
                        <label htmlFor="file">
                            <div className="btn-upload">
                                파일 업로드하기
                                {file && <span> - {file.name}</span>}
                            </div>
                        </label>
                        <input
                            type="file"
                            name="file"
                            id="file"
                            onChange={handleFileChange}
                            style={{ display: 'none' }}
                        />
                        <p style={{ fontSize: '12px', color: '#666', marginTop: '5px' }}>
                            * 최대 10MB, JPG/PNG/PDF 파일만 업로드 가능합니다.
                        </p>
                    </li>
                </ul>
                <ul className="auto-id-save auto-center">
                    <li className="auto-center">
                        <input
                            type="checkbox"
                            id="autologin"
                            name="auto"
                            checked={agreed}
                            onChange={(e) => setAgreed(e.target.checked)}
                        />
                        <label htmlFor="autologin" className="checkBox">
                            <span></span>
                            <em className="txt">개인정보 수집 및 이용에 동의합니다 *</em>
                        </label>
                    </li>
                </ul>
                <div className="bwhalf mt40">
                    <button type="button" className="btnXl btnMainColor1" onClick={handleInquirySubmit}>
                        문의 접수
                    </button>
                </div>
            </section>
        </>
    );
}

// 1:1 문의 메인 컴포넌트 (탭 구조)
function InquirySection() {
    const [activeInquiryTab, setActiveInquiryTab] = useState('form'); // 'form' 또는 'search'

    return (
        <>
            <section className="shadowBox3">
                <ul className="my-head-notice">
                    <li className="pos-left">
                        <ul className="tab-style-round">
                            <li className={activeInquiryTab === 'form' ? 'active' : ''}>
                                <a
                                    href=""
                                    onClick={(e) => {
                                        e.preventDefault();
                                        setActiveInquiryTab('form');
                                    }}
                                >
                                    1:1문의하기
                                </a>
                            </li>
                            <li className={activeInquiryTab === 'search' ? 'active' : ''}>
                                <a
                                    href=""
                                    onClick={(e) => {
                                        e.preventDefault();
                                        setActiveInquiryTab('search');
                                    }}
                                >
                                    내 문의 조회
                                </a>
                            </li>
                        </ul>
                    </li>
                </ul>
            </section>

            {activeInquiryTab === 'form' ? <InquiryFormSection /> : <InquirySearchSection />}
        </>
    );
}

// 사이드바 컴포넌트
function Sidebar({ activeMenu, setActiveMenu }) {
    const menuItems = [
        { id: 'notice', label: '업데이트 소식', active: activeMenu === 'notice' },
        { id: 'faq', label: '자주하는 질문', active: activeMenu === 'faq' },
        { id: 'inquiry', label: '문의하기', active: activeMenu === 'inquiry' }
    ];

    return (
        <div className="my-lnb-sidebar">
            <dl className="user-greeting">
                <dt>포트폴리오 FAQ</dt>
                <dd>궁금한 것을 찾아보세요</dd>
            </dl>
            <div className="code-customer-menu">
                {menuItems.map(item => (
                    <a
                        key={item.id}
                        href=""
                        className={item.active ? 'active' : ''}
                        onClick={(e) => {
                            e.preventDefault();
                            setActiveMenu(item.id);
                        }}
                    >
                        <i></i>{item.label}
                    </a>
                ))}
            </div>
        </div>
    );
}

// 메인 컴포넌트
function InquiryForm() {
    const [activeMenu, setActiveMenu] = useState('notice');

    const renderContent = () => {
        switch(activeMenu) {
            case 'notice':
                return <NoticeSection />;
            case 'faq':
                return <FaqSection />;
            case 'inquiry':
                return <InquirySection />;
            default:
                return <NoticeSection />;
        }
    };

    return (
        <div className="outer-layout-bg">
            <div className="mypage-2col-container">
                <Sidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} />
                <div className="my-main-content">
                    {renderContent()}
                </div>
            </div>
        </div>
    );
}

export default InquiryForm;
