import React, { useState } from 'react';
// CSS 파일들을 App.js에서만 import
import './styles/style.css';
import './styles/popup.css';
import './styles/webfont.css';
import './styles/info_style.css';
import './styles/common.css';

import GnbHeader from './components/GnbHeader';
import Home from './routes/Home';
import Gallery from './routes/Gallery';
import InquiryForm3 from './routes/InquiryForm3';
import MobileNav from './routes/MobileNav';

function App() {
    const [activeTab, setActiveTab] = useState('홈');
    const [homeSection, setHomeSection] = useState('notice');

    const handleTabClick = (tabName) => {
        setActiveTab(tabName);
    };

    const handleHomeSectionChange = (section) => {
        setHomeSection(section);
    };

// App.js
    const renderMainContent = () => {
        switch(activeTab) {
            case '홈':
                return <Home homeSection={homeSection} setHomeSection={setHomeSection} />;
            case '추첨형':
                return <Gallery />;
            case '즉석 추첨형':
                return (
                    <div className="space-body-top w1100">
                        <InquiryForm3 />
                    </div>
                );
            default:
                return <Home homeSection={homeSection} setHomeSection={setHomeSection} />;
        }
    };

    return (
        <div id="container">
            <GnbHeader activeTab={activeTab} onTabClick={handleTabClick} />
            {renderMainContent()}

            <MobileNav
                activeTab={activeTab}
                onTabClick={handleTabClick}
                onHomeSectionChange={handleHomeSectionChange}
            />
        </div>
    );


    <MobileNav
        activeTab={activeTab}
        onTabClick={handleTabClick}
        onHomeSectionChange={handleHomeSectionChange}
        homeSection={homeSection} // 추가
    />
}

export default App;
