import React, { useState } from 'react';
import Footer from './routes/Footer';
import GnbHeader from './components/GnbHeader';
import InquiryForm from './routes/InquiryForm';
import InquiryForm2 from './routes/InquiryForm2';
import MobileNav from './routes/MobileNav';

function App() {
  const [activeTab, setActiveTab] = useState('홈'); // 기본값을 '홈'으로 설정

  const handleTabClick = (tabName) => {
    setActiveTab(tabName);
  };

  const renderMainContent = () => {
    switch(activeTab) {
      case '홈':
        return <InquiryForm />;
      case '추첨형':
        return <InquiryForm2 />;
      case '즉석 추첨형':
        return <div>즉석 추첨형 페이지</div>;
      default:
        return <InquiryForm />; // 기본값
    }
  };

  return (
      <>
        <h1 style={{ color: 'red' }}>React 앱 정상 실행됨</h1>

        {/* 헤더에 탭 클릭 기능 전달 */}
        <GnbHeader activeTab={activeTab} onTabClick={handleTabClick} />

        {/* 탭에 따라 메인 컨텐츠 변경 */}
        <main>
          {renderMainContent()}
        </main>

        {/* 항상 보여지는 컴포넌트들 */}
        <Footer />
        <MobileNav />
      </>
  );
}

export default App;
