import React, { useState } from 'react';
// JSON 데이터 import - 변수명과 함수에서 사용하는 변수명을 일치시켜야 함
import portfolioData from '../data/portfolioData.json'; // portfolioWeb → portfolioData로 변경

function InquiryForm2() {
    const [activeFilter, setActiveFilter] = useState('웹디자인');
    const [currentItemIndex, setCurrentItemIndex] = useState(0);
    const [comments, setComments] = useState({});
    const [newComment, setNewComment] = useState('');

    const handleFilterClick = (filterName) => {
        setActiveFilter(filterName);
        setCurrentItemIndex(0);
    };

    // JSON 데이터에서 현재 카테고리 데이터 가져오기
    const getCurrentItems = () => {
        return portfolioData[activeFilter] || [];
    };

    const currentItems = getCurrentItems();
    const currentItem = currentItems[currentItemIndex];

    // 댓글 데이터 (프로젝트별)
    const getCommentsForItem = (itemId) => {
        const defaultComments = {
            // 웹디자인 프로젝트 댓글
            6: [
                { id: 1, username: '웹디자이너', timeAgo: '2시간 전', content: '커피 브랜드 디자인이 정말 세련되네요! 색감이 너무 예뻐요', avatar: '웹' },
                { id: 2, username: '카페사장', timeAgo: '3시간 전', content: '우리 카페에도 이런 디자인 적용하고 싶어요', avatar: '카' },
                { id: 3, username: '디자인러버', timeAgo: '5시간 전', content: '애니메이션 효과가 인상적이에요!', avatar: '디' }
            ],
            7: [
                { id: 4, username: '여행매니아', timeAgo: '1시간 전', content: '아시아 여행 사이트 너무 잘 만드셨네요!', avatar: '여' },
                { id: 5, username: '개발자', timeAgo: '4시간 전', content: 'jQuery 활용도 좋고 반응형도 잘 되어있어요', avatar: '개' }
            ],
            8: [
                { id: 6, username: '해리포터팬', timeAgo: '30분 전', content: '해리포터 웹사이트 마법같아요! 디자인이 환상적!', avatar: '해' },
                { id: 7, username: '영화러버', timeAgo: '2시간 전', content: '20주년 기념으로 딱이네요. 신비로운 느낌 굿!', avatar: '영' }
            ],

            // UX/UI 프로젝트 댓글
            0: [
                { id: 8, username: 'UX디자이너', timeAgo: '1시간 전', content: '두더지알람 UI가 정말 독창적이네요!', avatar: 'UX' },
                { id: 9, username: '앱개발자', timeAgo: '3시간 전', content: '사용자 인터페이스가 직관적이에요', avatar: '앱' }
            ],
            1: [
                { id: 10, username: '디자인학생', timeAgo: '2시간 전', content: '다양한 스킨 디자인이 창의적이에요!', avatar: '디' }
            ],
            2: [
                { id: 11, username: '여행앱기획자', timeAgo: '30분 전', content: 'KLOOK 모티브 좋네요! 실제 앱 같아요', avatar: '여' },
                { id: 12, username: '기획자', timeAgo: '1시간 전', content: '여행 일정 관리 UI가 깔끔해요', avatar: '기' }
            ],
            3: [
                { id: 13, username: '모바일러', timeAgo: '45분 전', content: '세부 화면까지 신경쓴 게 보여요', avatar: '모' }
            ],
            4: [
                { id: 14, username: 'NFT매니아', timeAgo: '1시간 전', content: 'NFT 플랫폼 UI 리뉴얼 완전 트렌디해요!', avatar: 'N' },
                { id: 15, username: '크립토러버', timeAgo: '2시간 전', content: '깔끔한 디자인이 사용하기 편할 것 같아요', avatar: '크' }
            ],
            5: [
                { id: 16, username: '블록체인러', timeAgo: '30분 전', content: '상세 화면도 일관성 있게 잘 만드셨네요', avatar: '블' }
            ],

            // 그래픽디자인 프로젝트 댓글
            20: [
                { id: 17, username: '사장님', timeAgo: '1시간 전', content: '닭발 전단지 색감이 식욕을 자극하네요!', avatar: '사' },
                { id: 18, username: '그래픽러', timeAgo: '2시간 전', content: '음식 사진을 잘 살린 디자인이에요', avatar: '그' }
            ],
            21: [
                { id: 19, username: '한식러버', timeAgo: '30분 전', content: '전통적인 분위기가 잘 표현됐어요', avatar: '한' }
            ],
            22: [
                { id: 20, username: '헬스매니아', timeAgo: '1시간 전', content: '운동 의욕이 생기는 디자인이네요!', avatar: '헬' }
            ],
            23: [
                { id: 21, username: '인테리어러', timeAgo: '2시간 전', content: '은은한 색감이 인테리어와 잘 어울려요', avatar: '인' }
            ],
            24: [
                { id: 22, username: '필라테스강사', timeAgo: '45분 전', content: '필라테스 분위기가 잘 살아있어요!', avatar: '필' }
            ],
            25: [
                { id: 23, username: '헬스장사장', timeAgo: '1시간 전', content: '고급스러운 느낌이 딱 우리 브랜드 같아요', avatar: '헬' }
            ],
            26: [
                { id: 24, username: '한옥러버', timeAgo: '30분 전', content: '캘리그라피가 정말 멋져요!', avatar: '한' }
            ],
            27: [
                { id: 25, username: '명함전문가', timeAgo: '2시간 전', content: '시인성도 좋고 디자인도 깔끔해요', avatar: '명' }
            ],
            28: [
                { id: 26, username: '문화센터장', timeAgo: '1시간 전', content: '다용도로 활용할 수 있어서 실용적이네요', avatar: '문' }
            ],
            29: [
                { id: 27, username: '카페사장님', timeAgo: '45분 전', content: '귀여운 일러스트가 매력적이에요!', avatar: '카' }
            ]
        };
        return comments[itemId] || defaultComments[itemId] || [];
    };

    const handlePrevItem = () => {
        if (currentItemIndex > 0) {
            setCurrentItemIndex(currentItemIndex - 1);
        }
    };

    const handleNextItem = () => {
        if (currentItemIndex < currentItems.length - 1) {
            setCurrentItemIndex(currentItemIndex + 1);
        }
    };

    const handleAddComment = () => {
        if (!newComment.trim() || !currentItem) return;

        const newCommentObj = {
            id: Date.now(),
            username: '나',
            timeAgo: '방금 전',
            content: newComment,
            avatar: '나'
        };

        const currentComments = comments[currentItem.id] || getCommentsForItem(currentItem.id);

        setComments(prev => ({
            ...prev,
            [currentItem.id]: [...currentComments, newCommentObj]
        }));
        setNewComment('');
    };

    // 카테고리별 개수 계산 - portfolioData 사용
    const getCategoryCount = (category) => {
        return portfolioData[category]?.length || 0;
    };

    // currentItems가 비어있거나 currentItem이 없는 경우 처리
    if (!currentItems.length || !currentItem) {
        return (
            <div className="outer-layout-bg">
                <div className="mypage-2col-container">
                    <div className="my-lnb-sidebar">
                        <dl className="user-greeting">
                            <dt>포트폴리오 갤러리</dt>
                            <dd>다양한 작품을 만나보세요</dd>
                        </dl>
                        <div className="code-customer-menu">
                            <a
                                href=""
                                className={activeFilter === '웹디자인' ? 'active' : ''}
                                onClick={(e) => {
                                    e.preventDefault();
                                    handleFilterClick('웹디자인');
                                }}
                            >
                                <i></i>웹디자인
                            </a>
                            <a
                                href=""
                                className={activeFilter === 'UX/UI디자인' ? 'active' : ''}
                                onClick={(e) => {
                                    e.preventDefault();
                                    handleFilterClick('UX/UI디자인');
                                }}
                            >
                                <i></i>UX/UI디자인
                            </a>
                            <a
                                href=""
                                className={activeFilter === '그래픽디자인' ? 'active' : ''}
                                onClick={(e) => {
                                    e.preventDefault();
                                    handleFilterClick('그래픽디자인');
                                }}
                            >
                                <i></i>그래픽디자인
                            </a>
                        </div>
                    </div>
                    <div className="my-main-content">
                        <h2 className="h3-title">포트폴리오 갤러리</h2>
                        <section className="shadowBox3">
                            <div style={{ padding: '50px', textAlign: 'center' }}>
                                <p>해당 카테고리에 작품이 없습니다.</p>
                            </div>
                        </section>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="outer-layout-bg">
            <div className="mypage-2col-container">
                {/* 왼쪽: 포트폴리오 카테고리 사이드바 */}
                <div className="my-lnb-sidebar">
                    <dl className="user-greeting">
                        <dt>포트폴리오 갤러리</dt>
                        <dd>다양한 작품을 만나보세요</dd>
                    </dl>
                    <div className="code-customer-menu">
                        <a
                            href=""
                            className={activeFilter === '웹디자인' ? 'active' : ''}
                            onClick={(e) => {
                                e.preventDefault();
                                handleFilterClick('웹디자인');
                            }}
                        >
                            <i></i>웹디자인 <span style={{
                            background: '#ff6b35',
                            color: 'white',
                            borderRadius: '10px',
                            padding: '2px 6px',
                            fontSize: '12px',
                            marginLeft: '5px'
                        }}>{getCategoryCount('웹디자인')}</span>
                        </a>
                        <a
                            href=""
                            className={activeFilter === 'UX/UI디자인' ? 'active' : ''}
                            onClick={(e) => {
                                e.preventDefault();
                                handleFilterClick('UX/UI디자인');
                            }}
                        >
                            <i></i>UX/UI디자인 <span style={{
                            background: '#ff6b35',
                            color: 'white',
                            borderRadius: '10px',
                            padding: '2px 6px',
                            fontSize: '12px',
                            marginLeft: '5px'
                        }}>{getCategoryCount('UX/UI디자인')}</span>
                        </a>
                        <a
                            href=""
                            className={activeFilter === '그래픽디자인' ? 'active' : ''}
                            onClick={(e) => {
                                e.preventDefault();
                                handleFilterClick('그래픽디자인');
                            }}
                        >
                            <i></i>그래픽디자인 <span style={{
                            background: '#ff6b35',
                            color: 'white',
                            borderRadius: '10px',
                            padding: '2px 6px',
                            fontSize: '12px',
                            marginLeft: '5px'
                        }}>{getCategoryCount('그래픽디자인')}</span>
                        </a>
                    </div>
                </div>

                {/* 오른쪽: 메인 컨텐츠 영역 */}
                <div className="my-main-content">
                    <h2 className="h3-title">포트폴리오 갤러리</h2>

                    <div style={{ display: 'flex', gap: '20px', height: '75vh' }}>
                        {/* 중앙: 현재 작품 */}
                        <div style={{ flex: 1, display: 'flex', flexDirection: 'column', minWidth: 0 }}>
                            <section className="shadowBox3" style={{ flex: 1, position: 'relative', width: '100%', overflow: 'hidden' }}>
                                <div className="slide-card-ty1 horizontal-card" style={{ width: '100%', height: '100%' }}>
                                    <div className="item" style={{
                                        height: '100%',
                                        width: '100%',
                                        display: 'flex',
                                        flexDirection: 'column',
                                        boxSizing: 'border-box'
                                    }}>
                                        <a href={currentItem.url} target="_blank" rel="noopener noreferrer" style={{
                                            width: '100%',
                                            height: '100%',
                                            display: 'flex',
                                            flexDirection: 'column',
                                            textDecoration: 'none'
                                        }}>
                                            <div className="badge-group">
                                                <p className={`badge-pos-left ${currentItem.badge}`}>{currentItem.badgeText}</p>
                                            </div>
                                            <div className="item-photo-box" style={{
                                                flex: 1,
                                                display: 'flex',
                                                alignItems: 'center',
                                                justifyContent: 'center',
                                                minHeight: '300px',
                                                background: '#f8f9fa'
                                            }}>
                                                <img src={currentItem.image} className="img" alt={currentItem.title} style={{
                                                    maxWidth: '100%',
                                                    maxHeight: '100%',
                                                    objectFit: 'contain'
                                                }} />
                                            </div>
                                            <ul className="item-info-box">
                                                <li className="mall-name">{currentItem.mallName}</li>
                                                <li className="campaign-title">{currentItem.title}</li>
                                                <li className="lottery-info">
                                                    <span className="participants">분류: <strong>{currentItem.type}</strong></span>
                                                    <span className="winners">상태: <strong>{currentItem.participants}</strong></span>
                                                </li>
                                                <li className="deadline">{currentItem.deadline}</li>
                                                <li style={{ marginTop: '10px', padding: '10px', background: '#f8f9fa', borderRadius: '5px' }}>
                                                    <p style={{ margin: 0, fontSize: '14px', lineHeight: '1.5', color: '#666' }}>
                                                        {currentItem.content}
                                                    </p>
                                                </li>
                                                <li style={{ marginTop: '10px' }}>
                                                    <div style={{ display: 'flex', flexWrap: 'wrap', gap: '5px' }}>
                                                        {currentItem.skills?.map((skill, index) => (
                                                            <span key={index} style={{
                                                                background: '#e9ecef',
                                                                color: '#495057',
                                                                padding: '4px 8px',
                                                                borderRadius: '12px',
                                                                fontSize: '12px',
                                                                fontWeight: '500'
                                                            }}>
                                                                {skill}
                                                            </span>
                                                        ))}
                                                    </div>
                                                </li>
                                            </ul>
                                        </a>
                                    </div>
                                </div>

                                {/* 네비게이션 버튼 */}
                                {currentItemIndex > 0 && (
                                    <button
                                        onClick={handlePrevItem}
                                        style={{
                                            position: 'absolute',
                                            left: '10px',
                                            top: '50%',
                                            transform: 'translateY(-50%)',
                                            background: 'rgba(0,0,0,0.6)',
                                            color: 'white',
                                            border: 'none',
                                            borderRadius: '50%',
                                            width: '45px',
                                            height: '45px',
                                            cursor: 'pointer',
                                            fontSize: '20px',
                                            display: 'flex',
                                            alignItems: 'center',
                                            justifyContent: 'center',
                                            zIndex: 10
                                        }}
                                    >
                                        ‹
                                    </button>
                                )}

                                {currentItemIndex < currentItems.length - 1 && (
                                    <button
                                        onClick={handleNextItem}
                                        style={{
                                            position: 'absolute',
                                            right: '10px',
                                            top: '50%',
                                            transform: 'translateY(-50%)',
                                            background: 'rgba(0,0,0,0.6)',
                                            color: 'white',
                                            border: 'none',
                                            borderRadius: '50%',
                                            width: '45px',
                                            height: '45px',
                                            cursor: 'pointer',
                                            fontSize: '20px',
                                            display: 'flex',
                                            alignItems: 'center',
                                            justifyContent: 'center',
                                            zIndex: 10
                                        }}
                                    >
                                        ›
                                    </button>
                                )}

                                {/* 인디케이터 */}
                                <div style={{
                                    position: 'absolute',
                                    bottom: '15px',
                                    left: '50%',
                                    transform: 'translateX(-50%)',
                                    display: 'flex',
                                    gap: '8px',
                                    zIndex: 10
                                }}>
                                    {currentItems.map((_, index) => (
                                        <div
                                            key={index}
                                            style={{
                                                width: '10px',
                                                height: '10px',
                                                borderRadius: '50%',
                                                background: index === currentItemIndex ? '#007bff' : 'rgba(255,255,255,0.7)',
                                                cursor: 'pointer',
                                                border: '1px solid rgba(0,0,0,0.2)',
                                                transition: 'all 0.3s ease'
                                            }}
                                            onClick={() => setCurrentItemIndex(index)}
                                        />
                                    ))}
                                </div>
                            </section>
                        </div>

                        {/* 오른쪽: 댓글 영역 */}
                        <div style={{ width: '350px', flexShrink: 0 }}>
                            <section className="shadowBox3" style={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                                <div style={{ padding: '20px', borderBottom: '1px solid #eee' }}>
                                    <h3 style={{ margin: 0, fontSize: '18px', fontWeight: 'bold', color: '#333' }}>
                                        댓글 <span style={{ color: '#999', fontSize: '14px' }}>
                                            ({getCommentsForItem(currentItem.id).length})
                                        </span>
                                    </h3>
                                </div>

                                {/* 댓글 목록 */}
                                <div style={{
                                    flex: 1,
                                    overflowY: 'auto',
                                    padding: '10px 20px',
                                    maxHeight: 'calc(75vh - 200px)'
                                }}>
                                    {getCommentsForItem(currentItem.id).map(comment => (
                                        <div key={comment.id} style={{
                                            display: 'flex',
                                            gap: '12px',
                                            padding: '15px 0',
                                            borderBottom: '1px solid #f8f9fa'
                                        }}>
                                            <div style={{
                                                width: '40px',
                                                height: '40px',
                                                borderRadius: '50%',
                                                background: `linear-gradient(45deg, #667eea, #764ba2)`,
                                                display: 'flex',
                                                alignItems: 'center',
                                                justifyContent: 'center',
                                                color: 'white',
                                                fontSize: '14px',
                                                fontWeight: 'bold',
                                                flexShrink: 0
                                            }}>
                                                {comment.avatar}
                                            </div>
                                            <div style={{ flex: 1, minWidth: 0 }}>
                                                <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '5px' }}>
                                                    <span style={{ fontWeight: 'bold', fontSize: '14px', color: '#333' }}>{comment.username}</span>
                                                    <span style={{ fontSize: '12px', color: '#999' }}>{comment.timeAgo}</span>
                                                </div>
                                                <p style={{ margin: 0, fontSize: '14px', lineHeight: '1.5', color: '#555', wordBreak: 'break-word' }}>{comment.content}</p>
                                            </div>
                                        </div>
                                    ))}
                                </div>

                                {/* 댓글 작성 - 하단 고정 */}
                                <div style={{
                                    padding: '20px',
                                    borderTop: '1px solid #eee',
                                    background: '#fafafa',
                                    flexShrink: 0
                                }}>
                                    <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                                        <textarea
                                            value={newComment}
                                            onChange={(e) => setNewComment(e.target.value)}
                                            placeholder="작품에 대한 감상을 남겨주세요..."
                                            style={{
                                                width: '100%',
                                                minHeight: '80px',
                                                maxHeight: '120px',
                                                border: '1px solid #ddd',
                                                borderRadius: '8px',
                                                padding: '12px',
                                                resize: 'vertical',
                                                fontSize: '14px',
                                                background: 'white',
                                                boxSizing: 'border-box'
                                            }}
                                            onKeyPress={(e) => {
                                                if (e.key === 'Enter' && e.ctrlKey) {
                                                    handleAddComment();
                                                }
                                            }}
                                        />
                                        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                            <p style={{ margin: 0, fontSize: '12px', color: '#999' }}>
                                                Ctrl + Enter로 빠른 등록
                                            </p>
                                            <button
                                                onClick={handleAddComment}
                                                className="btnXl btnMainColor1"
                                                style={{
                                                    padding: '10px 20px',
                                                    fontSize: '14px'
                                                }}
                                                disabled={!newComment.trim()}
                                            >
                                                등록
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            </section>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default InquiryForm2;
