import React, { useState } from 'react';

function InstantLotteryFeed() {
    // SNS Ïä§ÌÉÄÏùº ÌîºÎìú Îç∞Ïù¥ÌÑ∞
    const feedData = [
        {
            id: 1,
            profileImage: "../static/images/profile-01.jpg",
            username: "ÎîîÏûêÏù∏Îü¨Î≤Ñ",
            timeAgo: "2ÏãúÍ∞Ñ Ï†Ñ",
            content: "Î™ÖÌï® Ïù¥ÎªêÏöî",
            likes: 24,
            comments: 8,
            isLiked: false
        },
        {
            id: 2,
            profileImage: "../static/images/profile-02.jpg",
            username: "ÌÅ¨Î¶¨ÏóêÏù¥ÌÑ∞ÍπÄ",
            timeAgo: "3ÏãúÍ∞Ñ Ï†Ñ",
            content: "Ïù¥ ÎîîÏûêÏù∏ Ïñ¥ÎñªÍ≤å Ìïú Í±¥Í∞ÄÏöî? Ï†ïÎßê ÍπîÎÅîÌïòÍ≥† ÏÑ∏Î†®Îêú Í≤É Í∞ôÏïÑÏöî. Ïª¨Îü¨ Ï°∞Ìï©ÎèÑ ÎÑàÎ¨¥ Ï¢ãÎÑ§Ïöî!",
            likes: 42,
            comments: 15,
            isLiked: true
        },
        {
            id: 3,
            profileImage: "../static/images/profile-03.jpg",
            username: "ÎπÑÏ¶àÎãàÏä§Îß®Î∞ï",
            timeAgo: "5ÏãúÍ∞Ñ Ï†Ñ",
            content: "Ïñ¥ÎñªÍ≤å ÏûëÏóÖÌïúÏßÄ Î¨∏ÏùòÎìúÎ†§ÎèÑ Îê†ÍπåÏöî?",
            likes: 18,
            comments: 5,
            isLiked: false
        },
        {
            id: 4,
            profileImage: "../static/images/profile-04.jpg",
            username: "ÎßàÏºÄÌåÖÏ†ÑÎ¨∏Í∞Ä",
            timeAgo: "6ÏãúÍ∞Ñ Ï†Ñ",
            content: "Î∏åÎûúÎî© ÏûëÏóÖ Ï†ïÎßê Í∞êÍ∞ÅÏ†ÅÏù¥ÎÑ§Ïöî! Ïö∞Î¶¨ ÌöåÏÇ¨ÎèÑ Ïù¥Îü∞ Ïä§ÌÉÄÏùºÎ°ú Î¶¨Îâ¥ÏñºÌïòÍ≥† Ïã∂Ïñ¥Ïöî.",
            likes: 67,
            comments: 23,
            isLiked: true
        },
        {
            id: 5,
            profileImage: "../static/images/profile-05.jpg",
            username: "Ïä§ÌÉÄÌä∏ÏóÖCEO",
            timeAgo: "8ÏãúÍ∞Ñ Ï†Ñ",
            content: "Ìè¨Ìä∏Ìè¥Î¶¨Ïò§ Î≥¥Í≥† Ïó∞ÎùΩÎìúÎ†∏ÎäîÎç∞ ÎãµÎ≥ÄÏù¥ ÏóÜÏúºÏãúÎÑ§Ïöî „Ö†„Ö†",
            likes: 12,
            comments: 3,
            isLiked: false
        },
        {
            id: 6,
            profileImage: "../static/images/profile-06.jpg",
            username: "ÌîÑÎ¶¨ÎûúÏÑúÏù¥",
            timeAgo: "1Ïùº Ï†Ñ",
            content: "ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏Î∂ÑÎì§ Î∞òÏùëÏù¥ ÎÑàÎ¨¥ Ï¢ãÏïòÏñ¥Ïöî! ÎçïÎ∂ÑÏóê Ï∂îÍ∞Ä ÏûëÏóÖÎèÑ ÏùòÎ¢∞Î∞õÏïòÏäµÎãàÎã§. Í∞êÏÇ¨Ìï©ÎãàÎã§!",
            likes: 89,
            comments: 31,
            isLiked: true
        }
    ];

    const [feeds, setFeeds] = useState(feedData);
    const [newPost, setNewPost] = useState('');

    const handleLike = (feedId) => {
        setFeeds(feeds.map(feed => {
            if (feed.id === feedId) {
                return {
                    ...feed,
                    likes: feed.isLiked ? feed.likes - 1 : feed.likes + 1,
                    isLiked: !feed.isLiked
                };
            }
            return feed;
        }));
    };

    const handleNewPost = () => {
        if (!newPost.trim()) return;

        const newFeed = {
            id: feeds.length + 1,
            profileImage: "../static/images/profile-me.jpg",
            username: "ÎÇò",
            timeAgo: "Î∞©Í∏à Ï†Ñ",
            content: newPost,
            likes: 0,
            comments: 0,
            isLiked: false
        };

        setFeeds([newFeed, ...feeds]);
        setNewPost('');
    };

    return (
        <>
            <h2 className="h3-title">Ï¶âÏÑù Ï∂îÏ≤®Ìòï</h2>

            {/* ÏÉà Í≤åÏãúÎ¨º ÏûëÏÑ± */}
            <section className="shadowBox3" style={{ marginBottom: '20px' }}>
                <div style={{ padding: '20px' }}>
                    <div style={{ display: 'flex', alignItems: 'flex-start', gap: '15px' }}>
                        <div style={{
                            width: '50px',
                            height: '50px',
                            borderRadius: '50%',
                            background: '#f0f0f0',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            fontSize: '20px',
                            color: '#666'
                        }}>
                            üë§
                        </div>
                        <div style={{ flex: 1 }}>
                            <textarea
                                value={newPost}
                                onChange={(e) => setNewPost(e.target.value)}
                                placeholder="Î¨¥ÏóáÏù¥Îì† Î¨ºÏñ¥Î≥¥ÏÑ∏Ïöî..."
                                style={{
                                    width: '100%',
                                    minHeight: '80px',
                                    border: '1px solid #ddd',
                                    borderRadius: '8px',
                                    padding: '12px',
                                    resize: 'vertical',
                                    fontSize: '14px'
                                }}
                            />
                            <div style={{ marginTop: '10px', textAlign: 'right' }}>
                                <button
                                    onClick={handleNewPost}
                                    className="btnXl btnMainColor1"
                                    style={{ padding: '8px 20px' }}
                                >
                                    Í≤åÏãúÌïòÍ∏∞
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </section>

            {/* ÌîºÎìú Î™©Î°ù */}
            <section className="feed-container">
                {feeds.map(feed => (
                    <div key={feed.id} className="shadowBox3" style={{ marginBottom: '20px' }}>
                        <div style={{ padding: '20px' }}>
                            {/* ÌîÑÎ°úÌïÑ Ìó§Îçî */}
                            <div style={{
                                display: 'flex',
                                alignItems: 'center',
                                gap: '12px',
                                marginBottom: '15px'
                            }}>
                                <div style={{
                                    width: '45px',
                                    height: '45px',
                                    borderRadius: '50%',
                                    background: `linear-gradient(45deg, #${Math.floor(Math.random()*16777215).toString(16)}, #${Math.floor(Math.random()*16777215).toString(16)})`,
                                    display: 'flex',
                                    alignItems: 'center',
                                    justifyContent: 'center',
                                    color: 'white',
                                    fontSize: '16px',
                                    fontWeight: 'bold'
                                }}>
                                    {feed.username.charAt(0)}
                                </div>
                                <div>
                                    <h4 style={{
                                        margin: 0,
                                        fontSize: '16px',
                                        fontWeight: 'bold',
                                        color: '#333'
                                    }}>
                                        {feed.username}
                                    </h4>
                                    <p style={{
                                        margin: 0,
                                        fontSize: '13px',
                                        color: '#999'
                                    }}>
                                        {feed.timeAgo}
                                    </p>
                                </div>
                            </div>

                            {/* Í≤åÏãúÎ¨º ÎÇ¥Ïö© */}
                            <div style={{
                                marginBottom: '15px',
                                lineHeight: '1.5',
                                fontSize: '15px',
                                color: '#333'
                            }}>
                                {feed.content}
                            </div>

                            {/* ÏÉÅÌò∏ÏûëÏö© Î≤ÑÌäº */}
                            <div style={{
                                display: 'flex',
                                alignItems: 'center',
                                gap: '20px',
                                paddingTop: '15px',
                                borderTop: '1px solid #f0f0f0'
                            }}>
                                <button
                                    onClick={() => handleLike(feed.id)}
                                    style={{
                                        display: 'flex',
                                        alignItems: 'center',
                                        gap: '8px',
                                        background: 'none',
                                        border: 'none',
                                        cursor: 'pointer',
                                        color: feed.isLiked ? '#e74c3c' : '#666',
                                        fontSize: '14px',
                                        padding: '5px 10px',
                                        borderRadius: '20px',
                                        transition: 'background-color 0.2s'
                                    }}
                                    onMouseEnter={(e) => e.target.style.backgroundColor = '#f8f9fa'}
                                    onMouseLeave={(e) => e.target.style.backgroundColor = 'transparent'}
                                >
                                    <span style={{ fontSize: '16px' }}>
                                        {feed.isLiked ? '‚ù§Ô∏è' : 'ü§ç'}
                                    </span>
                                    <span>{feed.likes}</span>
                                </button>

                                <button style={{
                                    display: 'flex',
                                    alignItems: 'center',
                                    gap: '8px',
                                    background: 'none',
                                    border: 'none',
                                    cursor: 'pointer',
                                    color: '#666',
                                    fontSize: '14px',
                                    padding: '5px 10px',
                                    borderRadius: '20px',
                                    transition: 'background-color 0.2s'
                                }}
                                        onMouseEnter={(e) => e.target.style.backgroundColor = '#f8f9fa'}
                                        onMouseLeave={(e) => e.target.style.backgroundColor = 'transparent'}
                                >
                                    <span style={{ fontSize: '16px' }}>üí¨</span>
                                    <span>{feed.comments}</span>
                                </button>

                                <button style={{
                                    display: 'flex',
                                    alignItems: 'center',
                                    gap: '8px',
                                    background: 'none',
                                    border: 'none',
                                    cursor: 'pointer',
                                    color: '#666',
                                    fontSize: '14px',
                                    padding: '5px 10px',
                                    borderRadius: '20px',
                                    transition: 'background-color 0.2s'
                                }}
                                        onMouseEnter={(e) => e.target.style.backgroundColor = '#f8f9fa'}
                                        onMouseLeave={(e) => e.target.style.backgroundColor = 'transparent'}
                                >
                                    <span style={{ fontSize: '16px' }}>üì§</span>
                                    <span>Í≥µÏú†</span>
                                </button>
                            </div>
                        </div>
                    </div>
                ))}
            </section>

            {/* ÎçîÎ≥¥Í∏∞ Î≤ÑÌäº */}
            <div style={{ textAlign: 'center', marginTop: '30px' }}>
                <button className="btnXl btnMainColor1">
                    Îçî ÎßéÏùÄ Í≤åÏãúÎ¨º Î≥¥Í∏∞
                </button>
            </div>
        </>
    );
}

export default InstantLotteryFeed;
